import React from 'react';
import {
  Alert, CircularProgress, Container, Card, Box, Typography, Tabs, Tab, TextField, Button, TableContainer,
  Table,
  TableHead,
  TableBody,
  TableCell,
  TableRow
} from '@mui/material';
import axios from 'axios';

const MAX_PLAYERS = 5

const SERVER_URL = process.env.REACT_APP_SERVER_URL || 'http://172.20.10.4:8080';
const WS_URL = process.env.REACT_APP_WS_URL || 'ws://172.20.10.4:8080/ws';

const ws = new WebSocket(WS_URL);

function App() {
  const [gameId, setGameId] = React.useState(null)
  const [game, setGame] = React.useState(null)
  const [error, setError] = React.useState(null)

  const [activeTab, setActiveTab] = React.useState(0)
  const [playerId, setPlayerId] = React.useState(null)
  const [playerName, setPlayerName] = React.useState('')
  const [playerCount, setPlayerCount] = React.useState(MAX_PLAYERS)
  const [joinGameId, setJoinGameId] = React.useState('')
  const [roundCount, setRoundCount] = React.useState(5)
  const [timeLeave, setTimeLeave] = React.useState(120)
  const [count, setCount] = React.useState(1)
  const [showHelp, setShowHelp] = React.useState(false);

  React.useEffect(() => {
    // sync with server
    ws.onmessage = (msg) => {
      const data = JSON.parse(msg.data)
      if (data.gameId === gameId) {
        setGame(data)
      }
    }
    const handleKeyDown = (e) => {
      // 添加调试信息
      console.log('Key pressed:', e.key);
      console.log('Game status:', game?.status);
      console.log('Player ID:', playerId);

      if (e.key === 'v') {
        console.log(game);
        return;
      }

      // 首先验证游戏状态
      if (!gameId || !game || game.status !== 'in_progress') {
        console.log('Game not in progress');
        return;
      }

      // 验证当前玩家
      const player = game.players.find(player => player.id === playerId);
      if (!player) {
        console.log('Player not found');
        return;
      }

      // 验证玩家是否有球
      const balls = player.balls;
      const ball = balls[balls.length - 1];
      if (!ball) {
        console.log('No ball available');
        return;
      }

      // 验证球是否属于当前玩家
      if (ball.playerId !== playerId) {
        console.log('Ball belongs to another player');
        return;
      }

      // 处理具体的按键
      switch (e.key.toLowerCase()) {
        case 'r':
          if (ball.color === 'red') {
            handleColorMatch(ball);
          }
          break;
        case 'g':
          if (ball.color === 'green') {
            handleColorMatch(ball);
          }
          break;
        case 'b':
          if (ball.color === 'blue') {
            handleColorMatch(ball);
          }
          break;
        case 'enter':
          handleEnterKey(ball);
          break;
        case 'n':
          handleNewBall();
          break;
        case 'p':
          handlePoint();
          break;
      }
    }
    document.addEventListener('keydown', handleKeyDown)

    const timer = setInterval(() => {
      if (game && game.status === 'in_progress' && game.players.find(player => player.id === playerId).isOwn) {
        const newGame = { ...game }
        setTimeLeave(timeLeave - 1)
        if (timeLeave < 0 && newGame.currentRound < newGame.config.rounds) {
          newGame.currentRound += 1
          newGame.status = 'waiting'
          newGame.remainingTime = 120
          setTimeLeave(120)
        } else if (timeLeave < 0 && newGame.currentRound === newGame.config.rounds) {
          newGame.status = 'end'
          clearInterval(timer)
        }
        setGame(newGame)
        axios.post(`${SERVER_URL}/update_round_time_status`, {
          game_id: gameId,
          remainingTime: timeLeave,
          status: newGame.status,
          currentRound: newGame.currentRound
        })
      }
    }, 1000)
    return () => {
      clearInterval(timer)
      document.removeEventListener('keydown', handleKeyDown)
    }
  }, [game, gameId, playerId, timeLeave])

  const handleCreateGame = (e) => {
    e.preventDefault()
    axios.post(`${SERVER_URL}/create_game`, {
      name: playerName,
      player_count: playerCount,
      round_count: 5
    }).then(res => {
      setGameId(res.data.game.gameId)
      setGame(res.data.game)
      setPlayerId(res.data.playerId)
      setError(null)
    }).catch(err => {
      setError(err.message)
    })
  }

  const handleJoinGame = (e) => {
    e.preventDefault()
    axios.post(`${SERVER_URL}/join_game`, {
      name: playerName,
      game_id: joinGameId
    }).then(res => {
      setGameId(res.data.game.gameId)
      setGame(res.data.game)
      setPlayerId(res.data.playerId)
      setError(null)
    }).catch(err => {
      setError(err.message)
    })
  }

  const startGame = () => {
    // players should be more than 1
    if (game.players.length < game.config.playerCount) {
      setError(`Minimum ${game.config.playerCount} players required to start the game`)
      return
    }
    let flag = 0
    // players positon should be unique
    // players position should be between 1 and 20
    // players position should be at least 2 blocks apart
    game.players.forEach((player, index) => {
      if (player.position < 1 || player.position > game.config.blocks) {
        flag = 1
      }
      if (index > 0 && Math.abs(player.position - game.players[index - 1].position) < 2) {
        flag = 2
      }
    })
    if (flag === 1) {
      setError('Invalid player position')
      return
    }
    if (flag === 2) {
      setError('Players should be at least 2 blocks apart')
      return
    }
    setTimeLeave(120) // Reset time to 120 seconds when starting the game
    const newGame = { ...game };
    newGame.config.roundCount = roundCount;
    setGame(newGame);
    axios.post(`${SERVER_URL}/start_game`, {
      game_id: gameId,
      player_id: playerId,
      players: game.players,
      round_count: roundCount
    }).then(res => {
      setGame(res.data)
      setError(null)
    }).catch(err => {
      setError(err.message)
    })
  }

  const handleColorMatch = (ball) => {
    if (count === 2) {
      ball.color = game.config.balls[Math.floor(Math.random() * 3)];
      ball.position += 1;
      if (ball.position > game.config.blocks) {
        ball.position = 1;
      }
      setCount(1);
    } else {
      setCount(2);
    }

    // 更新游戏状态并确认
    axios.post(`${SERVER_URL}/update_game`, {
      game_id: gameId,
      players: game.players
    })
    .then(res => {
      console.log('Game state updated successfully');
      setGame(res.data);
    })
    .catch(err => {
      console.error('Failed to update game state:', err);
      setError(err.message);
    });
  };

  const handleEnterKey = (ball) => {
    if (!ball) return;

    const currentPlayer = game.players.find(player => player.id === playerId);
    const nextPlayer = game.players.find(player => player.position === ball.position);

    if (nextPlayer && nextPlayer.index !== currentPlayer.index + 1 && nextPlayer.index !== (currentPlayer.index - 1 || 1)) {
      if (ball.process.includes(nextPlayer.index) || ball.process.filter((e) => e === 1).length < 2) {
        ball.process.push(nextPlayer.index);
        ball.position = nextPlayer.position;
        ball.playerId = nextPlayer.id;
        ball.color = game.config.balls[Math.floor(Math.random() * 3)];
        nextPlayer.balls = [ball, ...nextPlayer.balls];
      }
    }

    // 从当前玩家的球列表中移除球
    const currentPlayerBalls = currentPlayer.balls;
    currentPlayerBalls.splice(currentPlayerBalls.length - 1, 1);

    axios.post(`${SERVER_URL}/update_game`, {
      game_id: gameId,
      players: game.players
    }).then(res => {
      console.log('Game state updated successfully');
      setGame(res.data);
    }).catch(err => {
      console.error('Failed to update game state:', err);
      setError(err.message);
    });
  };

  const handleNewBall = () => {
    const player = game.players.find(player => player.id === playerId);
    if (player.index === 1) {
      player.balls.push({
        color: game.config.balls[Math.floor(Math.random() * 3)],
        position: player.position,
        process: [player.index],
        playerId: player.id,
        index: player.index
      });
    }

    axios.post(`${SERVER_URL}/update_game`, {
      game_id: gameId,
      players: game.players
    }).then(res => {
      console.log('Game state updated successfully');
      setGame(res.data);
    }).catch(err => {
      console.error('Failed to update game state:', err);
      setError(err.message);
    });
  };

  const handlePoint = () => {
    const currentPlayer = game.players.find(p => p.id === playerId);
    if (currentPlayer && currentPlayer.index === 1) {
      const playerWithCompleteBall = game.players.find(player => 
        player.balls.some(b => 
          b.process && 
          b.process.sort().join('') === '1' + game.players.map(p => p.index).sort().join('')
        )
      );

      if (playerWithCompleteBall) {
        const newGame = { ...game };
        newGame.results[game.currentRound - 1].score += 1;

        const updatedPlayer = newGame.players.find(p => p.id === playerWithCompleteBall.id);
        if (updatedPlayer) {
          updatedPlayer.balls = updatedPlayer.balls.filter(b => 
            !b.process || 
            b.process.sort().join('') !== '1' + game.players.map(p => p.index).sort().join('')
          );
        }

        setGame(newGame);

        axios.post(`${SERVER_URL}/update_game`, {
          game_id: gameId,
          players: newGame.players
        }).then(res => {
          console.log('Game state updated successfully');
          setGame(res.data);
        }).catch(err => {
          console.error('Failed to update game state:', err);
          setError(err.message);
        });

        axios.post(`${SERVER_URL}/update_results`, {
          game_id: gameId,
          results: newGame.results
        }).then(res => {
          console.log('Results updated successfully');
          setGame(res.data);
        }).catch(err => {
          console.error('Failed to update results:', err);
          setError(err.message);
        });
      }
    }
  };

  const renderCircle = React.useCallback((index) => {
    let result = [];
    for (let i = 0; i < game.players.length; i++) {
      const player = game.players[i]
      for (let j = 0; j < game.players[i].balls.length; j++) {
        if (game.players[i].balls[j].position - 1 === index) {
          result.push(
            <Box
              key={game.players[i].balls[j].position}
              sx={{
                background: "gray",
                marginTop: "10px",
                padding: 2,
                display: 'flex',
                flexDirection: "row",
                alignItems: 'center',
                justifyContent: 'center'
              }}>
              <span>{player.name}:{game.players[i].balls[j].process.sort().join('') === '1' + game.players.map(player => player.index).sort().join('') ? 'end' : ''}</span>
              <Box sx={{ width: 20, height: 20, borderRadius: '50%', background: game.players[i].balls[j].color, ml: 1 }} >
              </Box>
            </Box>
          )
        }
      }
    }
    return result
  }, [game])
  const renderGameProgress = () => {
    const currentRoundResult = game.results[game.currentRound - 1] || { score: 0 };
    const currentPlayer = game.players.find(player => player.id === playerId);
    const currentBall = currentPlayer?.balls[currentPlayer.balls.length - 1];

    return (
      <Box sx={{ p: 3, backgroundColor: 'background.default', minHeight: '100vh' }}>
        <Box sx={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          mb: 3
        }}>
          <Box>
            <Typography variant="h4" sx={{ mb: 1, color: 'primary.main' }}>
              Game ID: {game.gameId} &nbsp;&nbsp; Status: {game.status === 'waiting' ? 'Waiting' : game.status === 'in_progress' ? 'In Progress' : 'Completed'}
            </Typography>
            <Typography variant="h5" sx={{ color: 'text.primary' }}>
              Player Name: {currentPlayer?.name}
            </Typography>
            <Typography variant="h5" sx={{ color: 'text.primary' }}>
              Current Round: {game.currentRound} / {game.config.rounds} Score: {currentRoundResult.score}
            </Typography>
            <Typography variant="h5" sx={{ color: 'text.primary' }}>
              Remaining time: {currentPlayer?.isOwn ? timeLeave : game.remainingTime - 1} seconds
            </Typography>
            {currentBall && (
              <Box sx={{ mt: 2, p: 2, border: '1px solid #ccc', borderRadius: 1 }}>
                <Typography variant="h6" sx={{ color: 'text.primary' }}>
                  Current Ball Status:
                </Typography>
                <Typography variant="body1">
                  Color: {currentBall.color}
                  <br />
                  Position: {currentBall.position}
                  <br />
                  Process: {currentBall.process.join(', ')}
                  <br />
                  Owner: {currentBall.playerId === playerId ? 'You' : 'Other Player'}
                </Typography>
              </Box>
            )}
          </Box>
        </Box>
        <Box className="flex justify-center items-center" sx={{ border: '1px solid #ccc', borderRadius: 1, p: 2 }}>
          <Box className="flex justify-start flex-wrap" sx={{ width: 1000, height: 1000, position: 'relative' }}>
            {
              Array(game.config.blocks).fill(0).map((_, index) => {
                const totalBlocks = game.config.blocks;
                const radius = 500; // 设定半径
                const angle = (index / totalBlocks) * 2 * Math.PI; // 计算角度
                const centerX = 540; // 圆心 X 坐标
                const centerY = 540; // 圆心 Y 坐标
                const x = centerX + radius * Math.cos(angle) - 50; // X 位置（调整偏移量）
                const y = centerY + radius * Math.sin(angle) - 50; // Y 位置（调整偏移量）

                return (
                  <Box key={index} className="flex flex-col justify-center items-center" sx={{
                    width: 100,
                    minHeight: 100,
                    border: '1px solid #ccc',
                    mr: 1,
                    mt: 1,
                    background: 'white',
                    position: 'absolute',
                    left: x,
                    top: y,
                  }}>
                    <Typography sx={{ color: 'black', whiteSpace: 'wrap', display: game.players.some(player => player.position - 1 === index) ? 'block' : 'none' }}>
                      Index: {game.players.some(player => player.position - 1 === index) ? game.players.find(player => player.position - 1 === index).index : ''}
                    </Typography>
                    <Typography sx={{ color: 'black', whiteSpace: 'wrap', display: 'block' }}>
                      {game.players.some(player => player.position - 1 === index) ? game.players.find(player => player.position - 1 === index).name : ''}
                    </Typography>
                    <Box sx={{ display: 'flex', flexDirection: "column", justifyContent: 'center', alignItems: 'center', width: "100%" }}>
                      {renderCircle(index).map((item) => item)}
                    </Box>
                  </Box>
                )
              })
            }
          </Box>
        </Box>
      </Box>
    )
  }

  const renderStatices = () => {
    return (
      <Box sx={{ p: 3, backgroundColor: 'background.default', minHeight: '100vh', display: "flex", flexDirection: "column" }}>
        <Typography variant="h4" sx={{ mb: 3, textAlign: 'center', color: 'primary.main' }}>
          Game Summary
        </Typography>
        <Button
          fullWidth
          variant="contained"
          type="submit"
          onClick={() => {
            setGameId(null)
            setGame(null)
            setPlayerId(null)
            setError(null)

            setActiveTab(0)
            setPlayerCount(MAX_PLAYERS)
            setPlayerName('')
            setRoundCount(5)
            setTimeLeave(120)
            setCount(1)
          }}
          sx={{ mb: 3 }}
        >
          Restart Game
        </Button>
        <TableContainer sx={{ mb: 2 }}>
          <Table size="medium">
            <TableHead>
              <TableRow>
                <TableCell>Round</TableCell>
                <TableCell align="right">Current Score</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {
                game.results.map((result, index) => (
                  <TableRow key={index}>
                    <TableCell component="th" scope="row">
                      Round {result.round}
                    </TableCell>
                    <TableCell align="right">{result.score}</TableCell>
                  </TableRow>
                ))
              }
            </TableBody>
          </Table>
        </TableContainer>
      </Box>
    )
  }

  const renderGame = () => {
    return (
      <Box sx={{ p: 3, backgroundColor: 'background.default', minHeight: '100vh' }}>
        <Box sx={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          mb: 3
        }}>
          <Box>
            <Typography variant="h4" sx={{ mb: 1, color: 'primary.main' }}>
              Game ID: {game.gameId} &nbsp;&nbsp;
              {
                game.players.some(player => player.id === playerId) && game.players.find(player => player.id === playerId).isOwn && (
                  <Button variant="contained" onClick={startGame} disabled={game.status !== 'waiting'}>
                    Start Game
                  </Button>
                )
              }
            </Typography>
            <Typography variant="h5" sx={{ color: 'text.primary' }}>
              Current Round: {game.currentRound}
            </Typography>
            <Typography variant="h5" sx={{ color: 'text.primary' }}>
              Status: {game.status === 'waiting' ? 'Waiting' : game.status === 'in_progress' ? 'In Progress' : 'Completed'}
            </Typography>
            <TextField
              fullWidth
              label="Round Score target"
              type="number"
              required
              value={roundCount}
              onChange={(e) => setRoundCount(Number(e.target.value))}
              helperText="Round Score target"
              sx={{ mb: 3, display: game.players.find(player => player.id === playerId).isOwn ? 'block' : 'none' }}
              autoComplete="off"
            />
          </Box>
        </Box>

        {/* 添加分数表格 */}
        <TableContainer sx={{ mb: 3, maxWidth: 400 }}>
          <Table size="medium">
            <TableHead>
              <TableRow>
                <TableCell>Round</TableCell>
                <TableCell align="right">Score</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {
                game.results.map((result, index) => (
                  <TableRow key={index}>
                    <TableCell component="th" scope="row">
                      Round {result.round}
                    </TableCell>
                    <TableCell align="right">{result.score}</TableCell>
                  </TableRow>
                ))
              }
            </TableBody>
          </Table>
        </TableContainer>

        <Box sx={{ border: '1px solid #ccc', borderRadius: 1, display: game.players.find(player => player.id === playerId).isOwn ? 'block' : 'none' }}>
          {
            game.players.map((player, index) => (
              <Box key={index} sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', mt: 1 }}>
                <Typography variant="h6" sx={{ color: 'text.primary' }}>
                  Player Name: {player.name} Index: {player.index}
                </Typography>
                <TextField
                  label="Position"
                  type="text"
                  value={player.position}
                  onChange={(e) => {
                    if (e.target.value < 1 || e.target.value > game.config.blocks) {
                      setError('Position must be between 1 and 20')
                      return
                    }
                    setError(null)
                    const newGame = { ...game }
                    newGame.players[index].position = Number(e.target.value)
                    // sort players by position
                    const players = JSON.parse(JSON.stringify(newGame.players)).sort((a, b) => a.position - b.position)
                    newGame.players.forEach((player, index) => {
                      players.forEach((p, i) => {
                        if (player.id === p.id) {
                          player.index = i + 1
                        }
                      })
                    })
                    setGame(newGame)
                    axios.post(`${SERVER_URL}/update_game`, {
                      game_id: gameId,
                      players: newGame.players
                    }).then(res => {
                      setGame(res.data)
                    }).catch(err => {
                      setError(err.message)
                    })
                  }}
                />
              </Box>
            ))
          }
        </Box>
        <Box className="flex justify-center items-center" sx={{ border: '1px solid #ccc', borderRadius: 1, p: 2 }}>
          <Box className="flex justify-start flex-wrap relative" sx={{ width: 1000, height: 1000, position: 'relative' }}>
            {
              Array(game.config.blocks).fill(0).map((_, index) => {
                const totalBlocks = game.config.blocks;
                const radius = 500; // 设定半径
                const angle = (index / totalBlocks) * 2 * Math.PI; // 计算角度
                const centerX = 540; // 圆心 X 坐标
                const centerY = 540; // 圆心 Y 坐标
                const x = centerX + radius * Math.cos(angle) - 50; // X 位置（调整偏移量）
                const y = centerY + radius * Math.sin(angle) - 50; // Y 位置（调整偏移量）

                return (
                  <Box key={index} className="flex flex-col justify-center items-center" sx={{
                    width: 100,
                    height: 100,
                    border: '1px solid #ccc',
                    mr: 1,
                    mt: 1,
                    background: game.players.some(player => player.position - 1 === index) ? 'blue' : 'white',
                    position: 'absolute',
                    left: x,
                    top: y,
                  }}>
                    <Typography sx={{ color: game.players.some(player => player.position - 1 === index) ? 'white' : 'black', whiteSpace: 'wrap' }}>
                      Index: {game.players.some(player => player.position - 1 === index) ? game.players.find(player => player.position - 1 === index).index : ''}
                    </Typography>
                    <Typography sx={{ color: game.players.some(player => player.position - 1 === index) ? 'white' : 'black', whiteSpace: 'wrap' }}>
                      Pos: {index + 1}
                    </Typography>
                    <Typography sx={{ color: 'white', whiteSpace: 'wrap' }}>
                      {game.players.some(player => player.position - 1 === index) ? game.players.find(player => player.position - 1 === index).name : ''}
                    </Typography>
                  </Box>
                )
              })
            }
          </Box>
        </Box>
      </Box>
    )
  }

  const renderMainMenu = () => {
    return (
      <Card sx={{
        maxWidth: 600,
        width: '100%',
        mx: 'auto',
        mt: 4
      }}>
        {/* Top section with title and subtitle */}
        <Box sx={{
          background: 'linear-gradient(180deg, #f5f9ff 0%, #e8f3ff 100%)',
          p: 4,
          borderTopLeftRadius: 8,
          borderTopRightRadius: 8
        }}>
          <Typography variant="h3" sx={{
            fontWeight: 'bold',
            background: 'linear-gradient(45deg, #2196F3 30%, #21CBF3 90%)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            textShadow: '2px 2px 4px rgba(0,0,0,0.1)',
            textAlign: 'center',
            mb: 1
          }}>
            Online Multiplayer Ball Point Game
          </Typography>
          <Button
            variant="outlined"
            onClick={() => setShowHelp(!showHelp)}
            sx={{ mt: 2 }}
          >
            {showHelp ? 'Hide Instructions' : 'Show Instructions'}
          </Button>
        </Box>

        {/* Help Dialog */}
        {showHelp && (
          <Box sx={{
            p: 3,
            backgroundColor: '#f8f9fa',
            borderBottom: '1px solid #dee2e6'
          }}>
            <Typography variant="h6" sx={{ mb: 2, color: 'primary.main' }}>
              Game Instructions
            </Typography>
            <Box sx={{ mb: 2 }}>
              <Typography variant="subtitle1" sx={{ fontWeight: 'bold' }}>
                Game Overview:
              </Typography>
              <Typography variant="body1">
                - This is a multiplayer game where players pass balls to each other
                - The goal is to complete ball passing sequences to earn points
                - Each round has a time limit
              </Typography>
            </Box>
            <Box sx={{ mb: 2 }}>
              <Typography variant="subtitle1" sx={{ fontWeight: 'bold' }}>
                Controls:
              </Typography>
              <Typography variant="body1">
                - Press 'R', 'G', 'B' to match ball colors (red, green, blue)
                - Press 'Enter' to pass the ball to the next player
                - Press 'N' to create a new ball (only for player 1)
                - Press 'P' to score points (only for player 1)
              </Typography>
            </Box>
            <Box sx={{ mb: 2 }}>
              <Typography variant="subtitle1" sx={{ fontWeight: 'bold' }}>
                Rules:
              </Typography>
              <Typography variant="body1">
                - Players must be at least 1 blocks apart
                - Balls must be passed in sequence
                - Only player 1 can create new balls and score points
                - Complete a full sequence to earn points
              </Typography>
            </Box>
          </Box>
        )}

        {/* Bottom section with form */}
        <Box sx={{
          background: '#ffffff',
          p: 4,
          borderBottomLeftRadius: 8,
          borderBottomRightRadius: 8
        }}>
          <Tabs value={activeTab} onChange={(e, newValue) => {
            setActiveTab(newValue);
            setPlayerName('');
            setPlayerCount(MAX_PLAYERS);
            setJoinGameId('');
            setGame({});
          }} sx={{ mb: 3 }}>
            <Tab label="CREATE GAME" />
            <Tab label="JOIN GAME" />
          </Tabs>

          {activeTab === 0 ? (
            // Create game form
            <Box component="form" onSubmit={handleCreateGame}>
              <TextField
                fullWidth
                label="Your Name"
                required
                value={playerName}
                onChange={(e) => {
                  if (e.target.value.length > 8) {
                    setError('Name cannot be more than 8 characters');
                  } else {
                    setError('');
                  }
                  setPlayerName(e.target.value)
                }}
                sx={{ mb: 2 }}
                autoComplete="off"
              />
              <TextField
                fullWidth
                label="Number of Players"
                type="number"
                required
                value={playerCount}
                onChange={(e) => setPlayerCount(Number(e.target.value))}
                helperText="Requiring 5 players"
                sx={{ mb: 3 }}
                autoComplete="off"
              />
              <Button
                fullWidth
                variant="contained"
                type="submit"
                disabled={!playerName || playerCount < MAX_PLAYERS || playerCount > 10}
              >
                Create Game
              </Button>
            </Box>
          ) : (
            // Join game form
            <Box component="form" onSubmit={handleJoinGame}>
              <TextField
                fullWidth
                label="Your Name"
                required
                value={playerName}
                onChange={(e) => setPlayerName(e.target.value)}
                sx={{ mb: 2 }}
                autoComplete="off"
              />
              <TextField
                fullWidth
                label="Game Code"
                required
                value={joinGameId}
                onChange={(e) => setJoinGameId(e.target.value)}
                sx={{ mb: 3 }}
                autoComplete="off"
              />
              <Button
                fullWidth
                variant="contained"
                type="submit"
                disabled={!playerName || !joinGameId}
              >
                Join Game
              </Button>
            </Box>
          )}
        </Box>
      </Card>
    )
  }

  return (
    <Container>
      {
        error && (
          <Alert severity="error" sx={{ mt: 2 }}>
            {error}
          </Alert>
        )
      }
      {
        !gameId ?
          renderMainMenu() :
          game.status === 'waiting' ?
            renderGame() :
            game.status === 'in_progress' ?
              renderGameProgress() :
              game.status === 'end' ? renderStatices() : <CircularProgress />
      }
    </Container>
  );
}

export default App;
