import axios from 'axios';
import { Game, Player } from '../types';

const API_URL = 'http://localhost:8000';

export const gameService = {
    createGame: async (playerCount: number, maxBatches: number): Promise<string> => {
        const response = await axios.post(`${API_URL}/games/create`, null, {
            params: { player_count: playerCount, max_batches: maxBatches }
        });
        return response.data.game_id;
    },

    joinGame: async (gameId: string, playerName: string): Promise<string> => {
        const response = await axios.post(`${API_URL}/games/${gameId}/join`, null, {
            params: { player_name: playerName }
        });
        return response.data.player_id;
    },

    startGame: async (gameId: string): Promise<void> => {
        await axios.post(`${API_URL}/games/${gameId}/start`);
    },

    getGameState: async (gameId: string): Promise<Game> => {
        const response = await axios.get(`${API_URL}/games/${gameId}`);
        return response.data;
    }
};

export class WebSocketService {
    private ws: WebSocket | null = null;
    private gameId: string;
    private playerId: string;
    private onGameUpdate: (game: Game) => void;

    constructor(gameId: string, playerId: string, onGameUpdate: (game: Game) => void) {
        this.gameId = gameId;
        this.playerId = playerId;
        this.onGameUpdate = onGameUpdate;
    }

    connect() {
        this.ws = new WebSocket(`ws://localhost:8000/ws/${this.playerId}`);

        this.ws.onmessage = (event) => {
            const message = JSON.parse(event.data);
            if (message.type === 'game_update' || message.type === 'game_started') {
                this.onGameUpdate(message.data);
            }
        };

        this.ws.onclose = () => {
            console.log('WebSocket connection closed');
            // 可以在这里添加重连逻辑
        };
    }

    completeTask(taskId: string) {
        if (this.ws && this.ws.readyState === WebSocket.OPEN) {
            this.ws.send(JSON.stringify({
                type: 'complete_task',
                game_id: this.gameId,
                task_id: taskId
            }));
        }
    }

    disconnect() {
        if (this.ws) {
            this.ws.close();
            this.ws = null;
        }
    }
}
