import React, { useState, useEffect } from 'react';
import {
    Box,
    Button,
    Card,
    CardContent,
    Typography,
    Grid,
    TextField,
    Dialog,
    DialogTitle,
    DialogContent,
    DialogActions,
    Tabs,
    Tab,
    Alert,
    Chip,
} from '@mui/material';
import { styled, keyframes } from '@mui/system';
import { Game as GameType, Player, TaskStatus, PlayerStatus, TaskType, Task } from '../types/game';
import { gameService, WebSocketService } from '../services/gameService';

interface GameProps {
    gameId?: string;
}

interface FlippingStates {
    [key: string]: {
        isFlipping: boolean;
        showOpposite: boolean;
    };
}

export const Game: React.FC<GameProps> = ({ gameId: initialGameId }) => {
    const [gameId, setGameId] = useState<string | undefined>(initialGameId);
    const [game, setGame] = useState<GameType | null>(null);
    const [playerName, setPlayerName] = useState('');
    const [playerId, setPlayerId] = useState<string | null>(null);
    const [wsService, setWsService] = useState<WebSocketService | null>(null);
    const [showJoinDialog, setShowJoinDialog] = useState(!initialGameId);
    const [playerCount, setPlayerCount] = useState(2);
    const [maxBatches, setMaxBatches] = useState(3);
    const [coinsPerBatch, setCoinsPerBatch] = useState(2);
    const [joinGameId, setJoinGameId] = useState('');
    const [error, setError] = useState<string | null>(null);
    const [activeTab, setActiveTab] = useState(0);
    const [startTime, setStartTime] = useState<number | null>(null);
    const [endTime, setEndTime] = useState<number | null>(null);
    const [showCompletionDialog, setShowCompletionDialog] = useState(false);
    const [currentTime, setCurrentTime] = useState<number>(0);
    const [showResultPage, setShowResultPage] = useState(false);

    useEffect(() => {
        if (gameId && playerId) {
            const service = new WebSocketService(gameId, playerId, (updatedGame) => {
                console.log('Game state updated:', updatedGame);
                setGame(updatedGame);
            });
            service.connect();
            setWsService(service);

            // Cleanup function
            return () => {
                service.disconnect();
            };
        }
    }, [gameId, playerId]);

    useEffect(() => {
        if (game?.status === 'in_progress' && !startTime) {
            setStartTime(Date.now());
        }
    }, [game?.status, startTime]);

    useEffect(() => {
        let timerInterval: NodeJS.Timeout | null = null;

        if (game?.status === 'in_progress' && startTime) {
            timerInterval = setInterval(() => {
                setCurrentTime(Date.now() - startTime);
            }, 1000);
        }

        if (game?.status === 'completed' && startTime && !endTime) {
            setEndTime(Date.now());
            setShowResultPage(true);
            if (timerInterval) {
                clearInterval(timerInterval);
            }
        }

        return () => {
            if (timerInterval) {
                clearInterval(timerInterval);
            }
        };
    }, [game?.status, startTime, endTime]);

    const handleCreateGame = async () => {
        if (!playerName) {
            setError('Please enter your name first');
            return;
        }

        try {
            const newGameId = await gameService.createGame(playerCount, maxBatches, coinsPerBatch);
            setGameId(newGameId);

            // Automatically join the game as the creator
            const newPlayerId = await gameService.joinGame(newGameId, playerName);
            setPlayerId(newPlayerId);

            // Get initial game state
            const gameState = await gameService.getGameState(newGameId);
            setGame(gameState);

            // Reset all input fields
            setPlayerName('');
            setPlayerCount(2);
            setMaxBatches(3);
            setCoinsPerBatch(2);
            setJoinGameId('');
            setError(null);
        } catch (error) {
            setError('Failed to create game. Please try again.');
            console.error('Error creating game:', error);
        }
    };

    const handleJoinGame = async () => {
        if (!playerName) {
            setError('Please enter player name');
            return;
        }

        const targetGameId = gameId || joinGameId;
        if (!targetGameId) {
            setError('Please enter game ID');
            return;
        }

        try {
            const exists = await gameService.checkGameExists(targetGameId);
            if (!exists) {
                setError('Game not found');
                return;
            }

            const newPlayerId = await gameService.joinGame(targetGameId, playerName);
            setPlayerId(newPlayerId);
            setGameId(targetGameId);
            setShowJoinDialog(false);
            const gameState = await gameService.getGameState(targetGameId);
            setGame(gameState);

            // Reset all input fields
            setPlayerName('');
            setPlayerCount(2);
            setMaxBatches(3);
            setCoinsPerBatch(2);
            setJoinGameId('');
            setError(null);
        } catch (error) {
            setError('Failed to join game. Please try again.');
            console.error('Error joining game:', error);
        }
    };

    const handleStartGame = async () => {
        if (!gameId) return;

        try {
            await gameService.startGame(gameId);
            setError(null);
        } catch (error) {
            setError('Failed to start game. Please try again.');
            console.error('Error starting game:', error);
        }
    };

    const flipAnimation = keyframes`
        0% {
            transform: rotateY(0deg);
        }
        49.9% {
            transform: rotateY(90deg);
            content: '';
        }
        50% {
            transform: rotateY(90deg);
            content: '';
        }
        100% {
            transform: rotateY(360deg);
        }
    `;

    const [flippingStates, setFlippingStates] = useState<FlippingStates>({});

    // Only reset flip states when the entire batch is completed
    useEffect(() => {
        if (game?.tasks) {
            // Group tasks by batch_id
            const batchTasks = new Map<number, Task[]>();
            game.tasks.forEach((task: Task) => {
                const tasks = batchTasks.get(task.batch_id) || [];
                tasks.push(task);
                batchTasks.set(task.batch_id, tasks);
            });

            // Check if any batch is fully completed
            batchTasks.forEach((tasks: Task[], batchId: number) => {
                const isAllCompleted = tasks.every((task: Task) => task.status === TaskStatus.COMPLETED);
                const hasNextPlayerTasks = game.tasks.some((task: Task) => 
                    task.batch_id > batchId && 
                    task.status === TaskStatus.PENDING
                );

                // Only clear flip states when batch is completed and next player has tasks
                if (isAllCompleted && hasNextPlayerTasks) {
                    const taskIds = tasks.map(t => t.id);
                    setFlippingStates(prev => {
                        const newStates = { ...prev };
                        taskIds.forEach(id => delete newStates[id]);
                        return newStates;
                    });
                }
            });
        }
    }, [game?.tasks]);

    const renderCoin = (type: TaskType, isFlipping: boolean, isSmall: boolean = false, onClick?: () => void, taskId?: string) => {
        const flipState = taskId ? flippingStates[taskId] : undefined;
        const task = taskId ? game?.tasks.find((t: Task) => t.id === taskId) : undefined;
        const displayType = flipState?.showOpposite
            ? (type === TaskType.HEADS ? TaskType.TAILS : TaskType.HEADS) 
            : type;

        // Only hide the coin if it's completed AND the entire batch is completed
        const currentBatch = task ? game?.tasks.filter((t: Task) => t.batch_id === task.batch_id) : [];
        const isBatchCompleted = currentBatch?.every((t: Task) => t.status === TaskStatus.COMPLETED);
        const hasNextPlayerTasks = task ? game?.tasks.some((t: Task) => 
            t.batch_id > task.batch_id && 
            t.status === TaskStatus.PENDING
        ) : false;

        const shouldHide = task?.status === TaskStatus.COMPLETED && isBatchCompleted && hasNextPlayerTasks;

        return (
            <Box
                sx={{
                    width: isSmall ? 20 : 32,
                    height: isSmall ? 20 : 32,
                    borderRadius: '50%',
                    display: shouldHide ? 'none' : 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    backgroundColor: displayType === TaskType.HEADS ? '#FFD700' : '#DAA520',
                    border: `${isSmall ? 1 : 2}px solid #B8860B`,
                    color: '#704214',
                    fontWeight: 'bold',
                    fontSize: isSmall ? '0.6rem' : '0.8rem',
                    animation: flipState?.isFlipping ? `${flipAnimation} 0.6s ease-in-out` : 'none',
                    perspective: 1000,
                    mb: isSmall ? 0 : 1,
                    position: 'relative',
                    cursor: onClick ? 'pointer' : 'default',
                    transition: 'transform 0.2s ease-in-out, box-shadow 0.2s ease-in-out',
                    '&:hover': onClick ? {
                        transform: 'scale(1.05)',
                        boxShadow: '0 2px 4px rgba(0,0,0,0.2)'
                    } : {},
                    '&:active': onClick ? {
                        transform: 'scale(0.95)'
                    } : {},
                    '&::after': {
                        content: '""',
                        position: 'absolute',
                        top: 0,
                        left: 0,
                        right: 0,
                        bottom: 0,
                        borderRadius: '50%',
                        background: 'linear-gradient(135deg, rgba(255,255,255,0.3) 0%, rgba(255,255,255,0) 50%, rgba(0,0,0,0.1) 100%)',
                        pointerEvents: 'none'
                    }
                }}
                onClick={onClick}
            >
                {displayType === TaskType.HEADS ? 'H' : 'T'}
            </Box>
        );
    };

    const handleCompleteTask = (taskId: string) => {
        if (wsService) {
            const task = game?.tasks.find(t => t.id === taskId);
            if (!task) return;

            // Start flipping animation
            setFlippingStates(prev => ({
                ...prev,
                [taskId]: { 
                    isFlipping: true, 
                    showOpposite: false
                }
            }));

            // After animation completes, keep showing the opposite side
            setTimeout(() => {
                setFlippingStates(prev => ({
                    ...prev,
                    [taskId]: { 
                        isFlipping: false, 
                        showOpposite: true
                    }
                }));
                wsService.completeTask(taskId);
            }, 600);
        }
    };

    const getPlayerStatusColor = (status: PlayerStatus) => {
        switch (status) {
            case PlayerStatus.ACTIVE:
                return 'success';
            case PlayerStatus.WAITING:
                return 'warning';
            case PlayerStatus.IDLE:
                return 'default';
            default:
                return 'default';
        }
    };

    const getTaskStatusColor = (status: TaskStatus) => {
        switch (status) {
            case TaskStatus.COMPLETED:
                return '#4caf50';
            case TaskStatus.IN_PROGRESS:
                return '#ff9800';
            case TaskStatus.PENDING:
                return '#9e9e9e';
            default:
                return '#9e9e9e';
        }
    };

    const renderTaskRow = (tasks: Task[], playerName: string) => {
        // 将任务分组为每行最多5个
        const rows: Task[][] = [];
        for (let i = 0; i < tasks.length; i += 5) {
            rows.push(tasks.slice(i, i + 5));
        }

        return rows.map((rowTasks, rowIndex) => (
            <Box 
                key={`row-${rowIndex}`} 
                sx={{ 
                    display: 'flex', 
                    gap: 1,
                    mb: 1,
                    flexWrap: 'nowrap',
                    justifyContent: 'flex-start'
                }}
            >
                {rowTasks.map(task => (
                    <Card 
                        key={`${task.id}-${playerName}-${task.batch_number}`} 
                        sx={{ 
                            width: 100,
                            minWidth: 100,
                            backgroundColor: 'background.paper',
                            position: 'relative',
                            overflow: 'visible'
                        }}
                    >
                        <CardContent sx={{ 
                            p: '8px !important',
                            pb: '8px !important',
                            display: 'flex',
                            flexDirection: 'column',
                            alignItems: 'center',
                        }}>
                            {renderCoin(
                                task.type, 
                                flippingStates[task.id]?.isFlipping || false,
                                false,
                                task.status === TaskStatus.PENDING && task.assigned_to === playerId && !flippingStates[task.id]?.isFlipping
                                    ? () => handleCompleteTask(task.id)
                                    : undefined,
                                task.id
                            )}
                            <Box sx={{ width: '100%', mt: 0.5 }}>
                                <Chip
                                    label={task.status}
                                    size="small"
                                    sx={{
                                        backgroundColor: getTaskStatusColor(task.status),
                                        color: 'white',
                                        fontWeight: 500,
                                        width: '100%',
                                        height: '20px',
                                        '& .MuiChip-label': {
                                            fontSize: '0.65rem',
                                            padding: '0 4px'
                                        }
                                    }}
                                />
                            </Box>
                        </CardContent>
                    </Card>
                ))}
            </Box>
        ));
    };

    const renderPlayer = (player: Player) => {
        const currentBatchTarget = player.current_tasks.length > 0 
            ? player.current_tasks[0].type 
            : player.completed_tasks.find(t => t.batch_number === player.current_batch)?.type;

        return (
            <Grid item xs={12} md={6} key={`player-${player.id}`}>
                <Card sx={{ height: '100%' }}>
                    <CardContent sx={{ p: 2 }}>
                        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 1 }}>
                            <Typography variant="h6" sx={{ 
                                color: 'primary.main',
                                fontSize: '1.1rem'
                            }}>{player.name}</Typography>
                            <Chip
                                label={player.status}
                                size="small"
                                color={getPlayerStatusColor(player.status)}
                                sx={{ 
                                    fontWeight: 500,
                                    height: '24px'
                                }}
                            />
                        </Box>
                        <Grid container spacing={1} sx={{ mb: 2 }}>
                            <Grid item xs={12}>
                                <Box sx={{ 
                                    display: 'flex', 
                                    alignItems: 'center', 
                                    gap: 1,
                                    mb: 1,
                                    backgroundColor: 'rgba(33, 150, 243, 0.1)',
                                    p: 1,
                                    borderRadius: 1
                                }}>
                                    <Typography variant="body2" sx={{ fontWeight: 500 }}>
                                        Current Batch: {player.current_batch} / {game?.max_batches}
                                    </Typography>
                                    {currentBatchTarget && (
                                        <Box sx={{ display: 'flex', alignItems: 'center', gap: 0.5 }}>
                                            <Typography variant="body2" sx={{ fontWeight: 500 }}>
                                                Target:
                                            </Typography>
                                            {renderCoin(currentBatchTarget, false, true)}
                                        </Box>
                                    )}
                                </Box>
                            </Grid>
                            <Grid item xs={6}>
                                <Typography variant="body2" color="textSecondary">Current Tasks</Typography>
                                <Typography variant="h6" sx={{ fontSize: '1.1rem' }}>{player.current_tasks.length}</Typography>
                            </Grid>
                            <Grid item xs={6}>
                                <Typography variant="body2" color="textSecondary">Completed</Typography>
                                <Typography variant="h6" sx={{ fontSize: '1.1rem' }}>{player.completed_tasks.length}</Typography>
                            </Grid>
                        </Grid>
                        <Box sx={{ mt: 2 }}>
                            <Typography variant="subtitle2" sx={{ color: 'primary.main', fontWeight: 500, mb: 1 }}>
                                Current Tasks
                            </Typography>
                            {renderTaskRow(player.current_tasks, player.name)}
                        </Box>
                        {player.completed_tasks.length > 0 && game && player.order === game.players.length - 1 && (
                            <Box sx={{ mt: 2 }}>
                                <Typography variant="subtitle2" sx={{ color: 'primary.main', fontWeight: 500, mb: 1 }}>
                                    Completed Tasks
                                </Typography>
                                {renderTaskRow(player.completed_tasks, player.name)}
                            </Box>
                        )}
                    </CardContent>
                </Card>
            </Grid>
        );
    };

    const handleTabChange = (_: React.SyntheticEvent, newValue: number) => {
        // Reset all fields when switching tabs
        setActiveTab(newValue);
        setPlayerName('');
        setPlayerCount(2);
        setMaxBatches(3);
        setCoinsPerBatch(2);
        setJoinGameId('');
        setError(null);
    };

    const handleGameComplete = () => {
        setEndTime(Date.now());
        setShowCompletionDialog(true);
    };

    const formatTime = (milliseconds: number) => {
        const seconds = Math.floor(milliseconds / 1000);
        const minutes = Math.floor(seconds / 60);
        const remainingSeconds = seconds % 60;
        return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
    };

    const renderCompletionDialog = () => {
        if (!startTime || !endTime) return null;
        const totalTime = endTime - startTime;

        return (
            <Dialog 
                open={showCompletionDialog} 
                onClose={() => setShowCompletionDialog(false)}
                PaperProps={{
                    sx: {
                        borderRadius: 2,
                        p: 2
                    }
                }}
            >
                <DialogTitle sx={{ textAlign: 'center', color: 'primary.main' }}>
                    Game Completed! 🎉
                </DialogTitle>
                <DialogContent>
                    <Box sx={{ 
                        display: 'flex', 
                        flexDirection: 'column', 
                        alignItems: 'center',
                        gap: 2,
                        py: 2
                    }}>
                        <Typography variant="h6">
                            Total Time: {formatTime(totalTime)}
                        </Typography>
                        <Typography variant="body1" color="text.secondary">
                            All players have successfully completed their tasks!
                        </Typography>
                    </Box>
                </DialogContent>
                <DialogActions sx={{ justifyContent: 'center' }}>
                    <Button 
                        variant="contained" 
                        onClick={() => {
                            setShowCompletionDialog(false);
                            window.location.reload(); // Reset the game
                        }}
                    >
                        Start New Game
                    </Button>
                </DialogActions>
            </Dialog>
        );
    };

    const handleCompleteGame = () => {
        setShowResultPage(true);
    };

    const handleReturnToMain = () => {
        window.location.reload();
    };

    const renderResultPage = () => {
        if (!startTime || !endTime) return null;
        const totalTime = endTime - startTime;

        return (
            <Box sx={{ 
                p: 3, 
                backgroundColor: 'background.default', 
                minHeight: '100vh',
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                justifyContent: 'center'
            }}>
                <Card sx={{ 
                    maxWidth: 600, 
                    width: '100%',
                    p: 4,
                    textAlign: 'center'
                }}>
                    <Typography variant="h4" sx={{ mb: 3, color: 'primary.main' }}>
                        Game Complete! 🎉
                    </Typography>
                    <Typography variant="h5" sx={{ mb: 4 }}>
                        Total Time: {formatTime(totalTime)}
                    </Typography>
                    <Button
                        variant="contained"
                        onClick={handleReturnToMain}
                        size="large"
                    >
                        Return to Main Menu
                    </Button>
                </Card>
            </Box>
        );
    };

    if (showResultPage) {
        return renderResultPage();
    }

    if (!gameId || !playerId) {
        return (
            <Box sx={{ 
                p: 3, 
                backgroundColor: 'background.default', 
                minHeight: '100vh',
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center'
            }}>
                <Box sx={{ 
                    textAlign: 'center', 
                    mb: 4,
                    mt: 4,
                    background: 'linear-gradient(45deg, #2196f3 30%, #21CBF3 90%)',
                    p: 3,
                    borderRadius: 2,
                    boxShadow: '0 3px 5px 2px rgba(33, 150, 243, .3)',
                    color: 'white',
                    width: '100%',
                    maxWidth: 800
                }}>
                    <Typography variant="h3" sx={{ fontWeight: 'bold', mb: 1 }}>
                        Online Multiplayer Coin Game
                    </Typography>
                    <Typography variant="subtitle1">
                        Flip coins with your friends in this multiplayer game!
                    </Typography>
                </Box>

                <Box sx={{ 
                    width: '100%', 
                    maxWidth: 800,
                    backgroundColor: 'white',
                    borderRadius: 2,
                    boxShadow: '0 3px 10px rgba(0,0,0,0.1)',
                    p: 3
                }}>
                    <Tabs 
                        value={activeTab} 
                        onChange={handleTabChange} 
                        sx={{ 
                            mb: 3,
                            borderBottom: 1,
                            borderColor: 'divider'
                        }}
                    >
                        <Tab label="Create Game" />
                        <Tab label="Join Game" />
                    </Tabs>

                    {error && (
                        <Alert severity="error" sx={{ mb: 2 }}>
                            {error}
                        </Alert>
                    )}

                    {activeTab === 0 ? (
                        <Grid container spacing={2}>
                            <Grid item xs={12}>
                                <TextField
                                    fullWidth
                                    label="Your Name"
                                    value={playerName}
                                    onChange={(e) => setPlayerName(e.target.value)}
                                    required
                                    autoComplete="off"
                                    sx={{ backgroundColor: 'white' }}
                                />
                            </Grid>
                            <Grid item xs={12} md={4}>
                                <TextField
                                    fullWidth
                                    label="Number of Players"
                                    type="number"
                                    value={playerCount}
                                    onChange={(e) => setPlayerCount(Number(e.target.value))}
                                    inputProps={{ min: 2, max: 5 }}
                                    autoComplete="off"
                                    sx={{ backgroundColor: 'white' }}
                                />
                            </Grid>
                            <Grid item xs={12} md={4}>
                                <TextField
                                    fullWidth
                                    label="Number of Batches"
                                    type="number"
                                    value={maxBatches}
                                    onChange={(e) => setMaxBatches(Number(e.target.value))}
                                    inputProps={{ min: 1, max: 10 }}
                                    autoComplete="off"
                                    sx={{ backgroundColor: 'white' }}
                                />
                            </Grid>
                            <Grid item xs={12} md={4}>
                                <TextField
                                    fullWidth
                                    label="Coins per Batch"
                                    type="number"
                                    value={coinsPerBatch}
                                    onChange={(e) => setCoinsPerBatch(Number(e.target.value))}
                                    inputProps={{ min: 1, max: 10 }}
                                    autoComplete="off"
                                    sx={{ backgroundColor: 'white' }}
                                />
                            </Grid>
                            <Grid item xs={12}>
                                <Button 
                                    variant="contained" 
                                    onClick={handleCreateGame}
                                    disabled={!playerName}
                                    sx={{ mt: 2 }}
                                    size="large"
                                >
                                    Create Game
                                </Button>
                            </Grid>
                        </Grid>
                    ) : (
                        <Grid container spacing={2}>
                            <Grid item xs={12} md={6}>
                                <TextField
                                    fullWidth
                                    label="Game ID"
                                    value={joinGameId}
                                    onChange={(e) => setJoinGameId(e.target.value.toUpperCase())}
                                    autoComplete="off"
                                    sx={{ backgroundColor: 'white' }}
                                />
                            </Grid>
                            <Grid item xs={12} md={6}>
                                <TextField
                                    fullWidth
                                    label="Player Name"
                                    value={playerName}
                                    onChange={(e) => setPlayerName(e.target.value)}
                                    autoComplete="off"
                                    sx={{ backgroundColor: 'white' }}
                                />
                            </Grid>
                            <Grid item xs={12}>
                                <Button 
                                    variant="contained" 
                                    onClick={handleJoinGame}
                                    sx={{ mt: 2 }}
                                    size="large"
                                >
                                    Join Game
                                </Button>
                            </Grid>
                        </Grid>
                    )}
                </Box>
            </Box>
        );
    }

    return (
        <Box sx={{ p: 3, backgroundColor: 'background.default', minHeight: '100vh' }}>
            {error && (
                <Alert severity="error" sx={{ mb: 2 }}>
                    {error}
                </Alert>
            )}

            {game && (
                <>
                    <Box sx={{ 
                        display: 'flex', 
                        justifyContent: 'space-between', 
                        alignItems: 'center',
                        mb: 3
                    }}>
                        <Box>
                            <Typography variant="h4" sx={{ mb: 1, color: 'primary.main' }}>
                                Game ID: {game.id}
                            </Typography>
                            <Typography variant="h5" sx={{ color: 'text.primary' }}>
                                Game Status: {game.status === 'waiting' ? 'Waiting' : game.status === 'in_progress' ? 'In Progress' : 'Completed'}
                            </Typography>
                        </Box>
                        {(game.status === 'in_progress' || game.status === 'completed') && startTime && (
                            <Typography variant="h6" sx={{ color: 'text.secondary' }}>
                                Time: {formatTime(game.status === 'completed' && endTime ? endTime - startTime : currentTime)}
                            </Typography>
                        )}
                    </Box>

                    {game.status === 'waiting' && playerId === game.players[0]?.id && (
                        <Button
                            variant="contained"
                            onClick={handleStartGame}
                            sx={{ mb: 3 }}
                        >
                            Start Game
                        </Button>
                    )}

                    <Grid container spacing={3}>
                        {game.players.map(renderPlayer)}
                    </Grid>
                </>
            )}
        </Box>
    );
};
