export enum TaskType {
    HEADS = 'HEADS',
    TAILS = 'TAILS'
}

export enum TaskStatus {
    PENDING = 'PENDING',
    IN_PROGRESS = 'IN_PROGRESS',
    COMPLETED = 'COMPLETED'
}

export enum PlayerStatus {
    WAITING = 'WAITING',
    ACTIVE = 'ACTIVE',
    IDLE = 'IDLE'
}

export interface Task {
    id: string;
    type: TaskType;
    description: string;
    status: TaskStatus;
    assigned_to?: string;
    batch_number: number;
    sub_batch_number: number;
}

export interface Player {
    id: string;
    name: string;
    current_tasks: Task[];
    completed_tasks: Task[];
    status: PlayerStatus;
    order: number;
    current_batch: number;
    current_sub_batch: number;
    task_queue: { [key: number]: Task[] };
    batch_start_time: { [key: number]: number };
    batch_completion_time: { [key: number]: number };
    sub_batch_completion_time: { [key: string]: number };
}

export interface Game {
    id: string;
    players: Player[];
    current_batch: number;
    current_sub_batch: number;
    max_batches: number;
    tasks_per_batch: number;
    tasks: Task[];
    status: string;
    active_player_index: number;
    current_player_order: number[];
    round_batches: { [key: number]: { num_batches: number; coins_per_batch: number } };
}
