import React, { useState, useEffect } from 'react';
import {
    Box,
    Button,
    Card,
    CardContent,
    Typography,
    Grid,
    TextField,
    Dialog,
    DialogTitle,
    DialogContent,
    DialogActions,
    Tabs,
    Tab,
    Alert,
    Chip,
    keyframes,
    Stack,
    TableContainer,
    Table,
    TableHead,
    TableBody,
    TableCell,
    TableRow,
    Paper
} from '@mui/material';
import { Game as GameType, Player, Task, TaskStatus, PlayerStatus, TaskType } from '../types';
import { gameService, WebSocketService } from '../services/gameService';

interface GameProps {
    gameId?: string;
}

export const Game: React.FC<GameProps> = ({ gameId: initialGameId }) => {
    const [gameId, setGameId] = useState<string | undefined>(initialGameId);
    const [game, setGame] = useState<GameType | null>(null);
    const [playerName, setPlayerName] = useState('');
    const [playerId, setPlayerId] = useState<string | null>(null);
    const [wsService, setWsService] = useState<WebSocketService | null>(null);
    const [showJoinDialog, setShowJoinDialog] = useState(!initialGameId);
    const [playerCount, setPlayerCount] = useState(2);
    const [maxBatches, setMaxBatches] = useState(3);
    const [coinsPerBatch, setCoinsPerBatch] = useState(2);
    const [joinGameId, setJoinGameId] = useState('');
    const [error, setError] = useState<string | null>(null);
    const [activeTab, setActiveTab] = useState(0);
    const [startTime, setStartTime] = useState<number | null>(null);
    const [endTime, setEndTime] = useState<number | null>(null);
    const [showCompletionDialog, setShowCompletionDialog] = useState(false);
    const [currentTime, setCurrentTime] = useState<number>(0);
    const [showResultPage, setShowResultPage] = useState(false);
    const [batchCompletionTimes, setBatchCompletionTimes] = useState<{
        [playerId: string]: {
            [batchNumber: number]: number;
        };
    }>({});
    const [batchStartTimes, setBatchStartTimes] = useState<{
        [playerId: string]: {
            [batchNum: number]: number;
        };
    }>({});
    const [batchFlipStates, setBatchFlipStates] = useState<{
        [batchNum: number]: {
            [taskId: string]: boolean;  // true means flipped
        };
    }>({});

    // 添加状态来跟踪硬币的翻转状态和批次完成状态
    const [flippedCoins, setFlippedCoins] = useState<string[]>([]);
    const [pendingBatchCompletion, setPendingBatchCompletion] = useState<{
        [batchNum: number]: string[];  // batchNum -> taskIds
    }>({});

    useEffect(() => {
        if (gameId && playerId) {
            const service = new WebSocketService(gameId, playerId, (updatedGame) => {
                console.log('Game state updated:', updatedGame);
                setGame(updatedGame);
            });
            service.connect();
            setWsService(service);

            // Cleanup function
            return () => {
                service.disconnect();
            };
        }
    }, [gameId, playerId]);

    useEffect(() => {
        if (game?.status === 'in_progress' && !startTime) {
            setStartTime(Date.now());
        }
    }, [game?.status, startTime]);

    useEffect(() => {
        let timerInterval: NodeJS.Timeout | null = null;

        if (game?.status === 'in_progress' && startTime) {
            timerInterval = setInterval(() => {
                setCurrentTime(Date.now() - startTime);
            }, 1000);
        }

        if (game?.status === 'completed' && startTime && !endTime) {
            setEndTime(Date.now());
            setShowResultPage(true);
            if (timerInterval) {
                clearInterval(timerInterval);
            }
        }

        return () => {
            if (timerInterval) {
                clearInterval(timerInterval);
            }
        };
    }, [game?.status, startTime, endTime]);

    useEffect(() => {
        if (game?.tasks && game.players) {
            const newStartTimes = { ...batchStartTimes };
            const newCompletionTimes = { ...batchCompletionTimes };

            game.players.forEach(player => {
                if (!newStartTimes[player.id]) {
                    newStartTimes[player.id] = {};
                }
                if (!newCompletionTimes[player.id]) {
                    newCompletionTimes[player.id] = {};
                }

                // 使用后端提供的批次开始时间
                if (player.batch_start_time) {
                    Object.entries(player.batch_start_time).forEach(([batchNum, startTime]) => {
                        newStartTimes[player.id][Number(batchNum)] = startTime;
                    });
                }

                // Calculate completion times for each batch
                for (let batchNum = 1; batchNum <= game.max_batches; batchNum++) {
                    const batchTasks = game.tasks.filter(t => 
                        t.batch_number === batchNum && 
                        t.assigned_to === player.id
                    );

                    const startedAt = newStartTimes[player.id]?.[batchNum];
                    if (startedAt && 
                        batchTasks.length > 0 && 
                        batchTasks.every(t => t.status === TaskStatus.COMPLETED) && 
                        !newCompletionTimes[player.id][batchNum]) {
                        newCompletionTimes[player.id][batchNum] = Date.now() - startedAt;
                    }
                }
            });

            setBatchStartTimes(newStartTimes);
            setBatchCompletionTimes(newCompletionTimes);
        }
    }, [game?.tasks, game?.players]);

    const handleCreateGame = async () => {
        if (!playerName) {
            setError('Please enter your name first');
            return;
        }

        try {
            const newGameId = await gameService.createGame(playerCount, maxBatches, coinsPerBatch);
            setGameId(newGameId);

            // Automatically join the game as the creator
            const newPlayerId = await gameService.joinGame(newGameId, playerName);
            setPlayerId(newPlayerId);

            // Get initial game state
            const gameState = await gameService.getGameState(newGameId);
            setGame(gameState);

            // Reset all input fields
            setPlayerName('');
            setPlayerCount(2);
            setMaxBatches(3);
            setCoinsPerBatch(2);
            setJoinGameId('');
            setError(null);
        } catch (error) {
            setError('Failed to create game. Please try again.');
            console.error('Error creating game:', error);
        }
    };

    const handleJoinGame = async () => {
        if (!playerName) {
            setError('Please enter player name');
            return;
        }

        const targetGameId = gameId || joinGameId;
        if (!targetGameId) {
            setError('Please enter game ID');
            return;
        }

        try {
            const exists = await gameService.checkGameExists(targetGameId);
            if (!exists) {
                setError('Game not found');
                return;
            }

            const newPlayerId = await gameService.joinGame(targetGameId, playerName);
            setPlayerId(newPlayerId);
            setGameId(targetGameId);
            setShowJoinDialog(false);
            const gameState = await gameService.getGameState(targetGameId);
            setGame(gameState);

            // Reset all input fields
            setPlayerName('');
            setPlayerCount(2);
            setMaxBatches(3);
            setCoinsPerBatch(2);
            setJoinGameId('');
            setError(null);
        } catch (error) {
            setError('Failed to join game. Please try again.');
            console.error('Error joining game:', error);
        }
    };

    const handleStartGame = async () => {
        if (!gameId) return;

        try {
            await gameService.startGame(gameId);
            setError(null);
        } catch (error) {
            setError('Failed to start game. Please try again.');
            console.error('Error starting game:', error);
        }
    };

    const flipAnimation = keyframes`
        0% {
            transform: rotateY(0deg);
        }
        49.9% {
            transform: rotateY(90deg);
            content: '';
        }
        50% {
            transform: rotateY(90deg);
            content: '';
        }
        100% {
            transform: rotateY(360deg);
        }
    `;

    // 添加一个专门用于渲染目标硬币的函数
    const renderTargetCoin = (type: TaskType) => {
        return (
            <Box
                sx={{
                    width: { xs: '20px', sm: '32px' },
                    height: { xs: '20px', sm: '32px' },
                    borderRadius: '50%',
                    border: theme => ({
                        xs: `1px solid ${theme.palette.primary.main}`,
                        sm: `2px solid ${theme.palette.primary.main}`
                    }),
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    backgroundColor: theme => theme.palette.background.paper,
                    color: theme => theme.palette.text.primary,
                    fontSize: { xs: '0.7rem', sm: '0.9rem' },
                    fontWeight: 'bold',
                    marginBottom: { xs: 0, sm: 1 },
                    marginRight: 1
                }}
            >
                {type === TaskType.HEADS ? 'H' : 'T'}
            </Box>
        );
    };

    // 检查批次是否所有硬币都已翻转
    const isBatchAllFlipped = (batchNum: number, playerId: string) => {
        const batchTasks = game?.tasks.filter(t => 
            t.batch_number === batchNum && 
            t.assigned_to === playerId
        );
        return batchTasks?.every(t => flippedCoins.includes(t.id)) || false;
    };

    // 处理硬币翻转
    const handleCoinFlip = (taskId: string) => {
        const task = game?.tasks.find(t => t.id === taskId);
        if (!task || !game) return;

        // 如果硬币已经翻转，不做任何操作
        if (flippedCoins.includes(taskId)) return;

        // 开始翻转动画
        setFlippingStates(prev => ({
            ...prev,
            [taskId]: { 
                isFlipping: true,
                showOpposite: false
            }
        }));

        // 动画完成后记录翻转状态
        setTimeout(() => {
            setFlippingStates(prev => ({
                ...prev,
                [taskId]: { 
                    isFlipping: false,
                    showOpposite: true
                }
            }));

            // 记录硬币已翻转
            setFlippedCoins(prev => [...prev, taskId]);

            // 更新待完成的批次
            setPendingBatchCompletion(prev => {
                const newState = { ...prev };
                if (!newState[task.batch_number]) {
                    newState[task.batch_number] = [];
                }
                if (!newState[task.batch_number].includes(taskId)) {
                    newState[task.batch_number] = [...newState[task.batch_number], taskId];
                }
                return newState;
            });

            // 检查是否所有硬币都已翻转
            if (task.assigned_to && isBatchAllFlipped(task.batch_number, task.assigned_to)) {
                handleCompleteBatch(task.batch_number);
            }
        }, 600);
    };

    // 处理批次完成
    const handleCompleteBatch = (batchNum: number) => {
        const pendingTasks = pendingBatchCompletion[batchNum] || [];
        if (pendingTasks.length === 0) return;

        // 发送所有任务完成的请求
        pendingTasks.forEach(taskId => {
            if (wsService) {
                wsService.completeTask(taskId);
            }
        });

        // 清理这个批次的待完成状态
        setPendingBatchCompletion(prev => {
            const newState = { ...prev };
            delete newState[batchNum];
            return newState;
        });
    };

    // 修改渲染硬币的函数
    const renderCoin = (type: TaskType, isFlipping: boolean, isSmall: boolean = false, onClick?: () => void, taskId?: string) => {
        const flipState = taskId ? batchFlipStates[taskId] : undefined;
        const isFlipped = taskId ? flippedCoins.includes(taskId) : false;

        // 决定显示哪一面
        const displayType = isFlipped
            ? (type === TaskType.HEADS ? TaskType.TAILS : TaskType.HEADS) 
            : type;

        return (
            <Box
                sx={{
                    width: isSmall ? 20 : 32,
                    height: isSmall ? 20 : 32,
                    borderRadius: '50%',
                    backgroundColor: displayType === TaskType.HEADS ? '#FFD700' : '#DAA520',
                    border: `${isSmall ? 1 : 2}px solid #B8860B`,
                    color: '#704214',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    fontWeight: 'bold',
                    fontSize: isSmall ? '0.6rem' : '0.8rem',
                    perspective: 1000,
                    mb: isSmall ? 0 : 1,
                    position: 'relative',
                    cursor: onClick ? 'pointer' : 'default',
                    transition: 'transform 0.6s',
                    transformStyle: 'preserve-3d',
                    transform: flipState?.isFlipping ? 'rotateY(360deg)' : 'rotateY(0deg)',
                    '&:hover': onClick ? {
                        transform: 'scale(1.05)',
                        boxShadow: '0 2px 4px rgba(0,0,0,0.2)'
                    } : {},
                    '&:active': onClick ? {
                        transform: 'scale(0.95)'
                    } : {},
                    '&::after': {
                        content: '""',
                        position: 'absolute',
                        top: 0,
                        left: 0,
                        right: 0,
                        bottom: 0,
                        borderRadius: '50%',
                        background: 'linear-gradient(135deg, rgba(255,255,255,0.3) 0%, rgba(255,255,255,0) 50%, rgba(0,0,0,0.1) 100%)',
                        pointerEvents: 'none'
                    }
                }}
                onClick={onClick}
            >
                {displayType === TaskType.HEADS ? 'H' : 'T'}
            </Box>
        );
    };

    // 监听游戏状态变化
    useEffect(() => {
        if (game?.tasks) {
            // 检查是否有新的批次开始
            game.players.forEach(player => {
                const playerTasks = game.tasks.filter(t => t.assigned_to === player.id);
                const currentBatchTasks = playerTasks.filter(t => t.batch_number === player.current_batch);

                // 如果有新的批次任务，且都是PENDING状态，说明是新批次开始
                if (currentBatchTasks.length > 0 && 
                    currentBatchTasks.every(t => t.status === TaskStatus.PENDING)) {
                    // 清理上一个批次的翻转状态
                    const prevBatchTasks = playerTasks.filter(t => t.batch_number === player.current_batch - 1);
                    const prevBatchTaskIds = prevBatchTasks.map(t => t.id);
                    setFlippedCoins(prev => prev.filter(id => !prevBatchTaskIds.includes(id)));
                }
            });
        }
    }, [game?.tasks]);

    const getPlayerStatusColor = (status: PlayerStatus) => {
        switch (status) {
            case PlayerStatus.ACTIVE:
                return 'success';
            case PlayerStatus.WAITING:
                return 'warning';
            case PlayerStatus.IDLE:
                return 'default';
            default:
                return 'default';
        }
    };

    const getTaskStatusColor = (status: TaskStatus) => {
        switch (status) {
            case TaskStatus.COMPLETED:
                return '#4caf50';
            case TaskStatus.IN_PROGRESS:
                return '#ff9800';
            case TaskStatus.PENDING:
                return '#9e9e9e';
            default:
                return '#9e9e9e';
        }
    };

    // 渲染任务行
    const renderTaskRow = (tasks: Task[]) => {
        return (
            <Box sx={{ 
                display: 'flex', 
                flexWrap: 'wrap',
                gap: 1,
                mb: 2 
            }}>
                {tasks.map(task => (
                    <Box
                        key={task.id}
                        sx={{
                            display: 'flex',
                            alignItems: 'center',
                        }}>
                        {renderCoin(
                            task.type,
                            false,
                            false,
                            task.assigned_to === playerId && !flippedCoins.includes(task.id)
                                ? () => handleCoinFlip(task.id)
                                : undefined,
                            task.id
                        )}
                    </Box>
                ))}
            </Box>
        );
    };

    // 渲染玩家卡片
    const renderPlayer = (player: Player) => {
        const currentBatchTasks = game?.tasks.filter(t => 
            t.batch_number === player.current_batch && 
            t.assigned_to === player.id
        ) || [];

        const completedTasks = game?.tasks.filter(t =>
            t.status === TaskStatus.COMPLETED &&
            t.assigned_to === player.id
        ) || [];

        // 获取当前批次的目标类型
        const currentBatchTarget = currentBatchTasks[0]?.type;

        return (
            <Card key={player.id} sx={{ mb: 2 }}>
                <CardContent>
                    <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
                        <Typography variant="h6">
                            {player.name}
                        </Typography>
                        <Chip 
                            label={player.status}
                            color={player.status === PlayerStatus.ACTIVE ? "success" : "default"}
                            size="small"
                        />
                    </Box>

                    <Box sx={{ mb: 2, bgcolor: 'background.default', p: 2, borderRadius: 1 }}>
                        <Typography variant="subtitle2" sx={{ mb: 1 }}>
                            Current Batch: {player.current_batch} / {game?.max_batches}
                            {currentBatchTarget && (
                                <Box sx={{ display: 'flex', alignItems: 'center', mt: 1 }}>
                                    <Typography variant="body2" sx={{ mr: 1 }}>
                                        Target:
                                    </Typography>
                                    {renderCoin(currentBatchTarget, false, true)}
                                </Box>
                            )}
                        </Typography>

                        <Box sx={{ mb: 2 }}>
                            <Typography variant="subtitle2" color="text.secondary">
                                Current Tasks
                            </Typography>
                            {renderTaskRow(currentBatchTasks)}
                        </Box>

                        {completedTasks.length > 0 && (
                            <Box>
                                <Typography variant="subtitle2" color="text.secondary">
                                    Completed Tasks
                                </Typography>
                                {renderTaskRow(completedTasks)}
                            </Box>
                        )}

                        {player.id === playerId && isBatchAllFlipped(player.current_batch, player.id) && (
                            <Button
                                variant="contained"
                                color="primary"
                                onClick={() => handleCompleteBatch(player.current_batch)}
                                sx={{ mt: 2 }}
                            >
                                Complete This Batch
                            </Button>
                        )}
                    </Box>
                </CardContent>
            </Card>
        );
    };

    const handleTabChange = (_: React.SyntheticEvent, newValue: number) => {
        // Reset all fields when switching tabs
        setActiveTab(newValue);
        setPlayerName('');
        setPlayerCount(2);
        setMaxBatches(3);
        setCoinsPerBatch(2);
        setJoinGameId('');
        setError(null);
    };

    const handleGameComplete = () => {
        setEndTime(Date.now());
        setShowCompletionDialog(true);
    };

    // 添加一个辅助函数来处理毫秒到时间的转换
    const formatTimeFromMs = (ms: number) => {
        const totalSeconds = Math.floor(ms / 1000);
        const minutes = Math.floor(totalSeconds / 60);
        const seconds = totalSeconds % 60;
        return `${minutes}:${seconds.toString().padStart(2, '0')}`;
    };

    const renderCompletionDialog = () => {
        if (!startTime || !endTime) return null;
        const totalTime = endTime - startTime;

        return (
            <Dialog 
                open={showCompletionDialog} 
                onClose={() => setShowCompletionDialog(false)}
                PaperProps={{
                    sx: {
                        borderRadius: 2,
                        p: 2
                    }
                }}
            >
                <DialogTitle sx={{ textAlign: 'center', color: 'primary.main' }}>
                    Game Completed!
                </DialogTitle>
                <DialogContent>
                    <Box sx={{ 
                        display: 'flex', 
                        flexDirection: 'column', 
                        alignItems: 'center',
                        gap: 2,
                        py: 2
                    }}>
                        <Typography variant="h6">
                            Total Time: {formatTimeFromMs(totalTime)}
                        </Typography>
                        <Typography variant="body1" color="text.secondary">
                            All players have successfully completed their tasks!
                        </Typography>
                    </Box>
                </DialogContent>
                <DialogActions sx={{ justifyContent: 'center' }}>
                    <Button 
                        variant="contained" 
                        onClick={() => {
                            setShowCompletionDialog(false);
                            window.location.reload(); // Reset the game
                        }}
                    >
                        Start New Game
                    </Button>
                </DialogActions>
            </Dialog>
        );
    };

    const handleCompleteGame = () => {
        setShowResultPage(true);
    };

    const handleReturnToMain = () => {
        window.location.reload();
    };

    const renderResultPage = () => {
        if (!startTime || !endTime) return null;
        const totalTime = endTime - startTime;

        return (
            <Box sx={{ 
                p: 3, 
                backgroundColor: 'background.default', 
                minHeight: '100vh',
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                justifyContent: 'center'
            }}>
                <Card sx={{ 
                    maxWidth: 600, 
                    width: '100%',
                    p: 4,
                    textAlign: 'center'
                }}>
                    <Typography variant="h4" sx={{ mb: 3, color: 'primary.main' }}>
                        Game Complete! 🎉
                    </Typography>
                    <Typography variant="h5" sx={{ mb: 4 }}>
                        Total Time: {formatTimeFromMs(totalTime)}
                    </Typography>
                    <Button
                        variant="contained"
                        onClick={handleReturnToMain}
                        size="large"
                    >
                        Return to Main Menu
                    </Button>
                </Card>
            </Box>
        );
    };

    const renderStatistics = () => {
        if (!game) return null;

        return (
            <Box sx={{ p: 2 }}>
                <Typography variant="h6" sx={{ mb: 2, color: 'primary.main' }}>
                    Game Statistics
                </Typography>
                {Array.from({ length: game.max_batches }, (_, i) => i + 1).map(batchNum => (
                    <Box key={batchNum} sx={{ mb: 3 }}>
                        <Typography variant="subtitle1" sx={{ fontWeight: 'bold', mb: 1 }}>
                            Batch {batchNum}
                        </Typography>
                        <TableContainer component={Paper} sx={{ mb: 2 }}>
                            <Table size="small">
                                <TableHead>
                                    <TableRow>
                                        <TableCell>Player</TableCell>
                                        <TableCell align="right">Time</TableCell>
                                        <TableCell align="right">Status</TableCell>
                                    </TableRow>
                                </TableHead>
                                <TableBody>
                                    {game.players.map(player => {
                                        const batchTasks = game.tasks.filter(t => 
                                            t.batch_number === batchNum && 
                                            t.assigned_to === player.id
                                        );
                                        const isCompleted = batchTasks.length > 0 && batchTasks.every(t => t.status === TaskStatus.COMPLETED);
                                        const hasStarted = player.batch_start_time?.[batchNum] != null;
                                        const completionTime = player.batch_completion_time?.[batchNum];
                                        const startTime = player.batch_start_time?.[batchNum];

                                        // 计算当前时间（如果批次已开始但未完成）
                                        let displayTime = '-';
                                        if (completionTime) {
                                            displayTime = formatTimeFromMs(completionTime);
                                        } else if (hasStarted && startTime) {
                                            const currentTime = Date.now() - startTime;
                                            displayTime = formatTimeFromMs(currentTime);
                                        }

                                        let status = 'Pending';
                                        if (isCompleted) {
                                            status = 'Completed';
                                        } else if (hasStarted) {
                                            status = 'In Progress';
                                        }

                                        return (
                                            <TableRow key={player.id}>
                                                <TableCell>{player.name}</TableCell>
                                                <TableCell align="right">{displayTime}</TableCell>
                                                <TableCell align="right">
                                                    <Chip 
                                                        label={status}
                                                        size="small"
                                                        color={isCompleted ? 'success' : (hasStarted ? 'primary' : 'default')}
                                                    />
                                                </TableCell>
                                            </TableRow>
                                        );
                                    })}
                                    <TableRow sx={{ backgroundColor: 'rgba(0, 0, 0, 0.04)' }}>
                                        <TableCell><strong>Total</strong></TableCell>
                                        <TableCell align="right">
                                            <strong>
                                                {formatTimeFromMs(game.players.reduce((sum, p) => 
                                                    sum + (p.batch_completion_time?.[batchNum] || 0), 
                                                    0
                                                ))}
                                            </strong>
                                        </TableCell>
                                        <TableCell />
                                    </TableRow>
                                </TableBody>
                            </Table>
                        </TableContainer>
                    </Box>
                ))}
            </Box>
        );
    };

    if (showResultPage) {
        return renderResultPage();
    }

    if (!gameId || !playerId) {
        return (
            <Box sx={{ 
                p: 3, 
                backgroundColor: 'background.default', 
                minHeight: '100vh',
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center'
            }}>
                <Box sx={{ 
                    textAlign: 'center', 
                    mb: 4,
                    mt: 4,
                    background: 'linear-gradient(45deg, #2196f3 30%, #21CBF3 90%)',
                    p: 3,
                    borderRadius: 2,
                    boxShadow: '0 3px 5px 2px rgba(33, 150, 243, .3)',
                    color: 'white',
                    width: '100%',
                    maxWidth: 800
                }}>
                    <Typography variant="h3" sx={{ fontWeight: 'bold', mb: 1 }}>
                        Online Multiplayer Coin Game
                    </Typography>
                    <Typography variant="subtitle1">
                        Flip coins with your friends in this multiplayer game!
                    </Typography>
                </Box>

                <Box sx={{ 
                    width: '100%', 
                    maxWidth: 800,
                    backgroundColor: 'white',
                    borderRadius: 2,
                    boxShadow: '0 3px 10px rgba(0,0,0,0.1)',
                    p: 3
                }}>
                    <Tabs 
                        value={activeTab} 
                        onChange={handleTabChange} 
                        sx={{ 
                            mb: 3,
                            borderBottom: 1,
                            borderColor: 'divider'
                        }}
                    >
                        <Tab label="Create Game" />
                        <Tab label="Join Game" />
                    </Tabs>

                    {error && (
                        <Alert severity="error" sx={{ mb: 2 }}>
                            {error}
                        </Alert>
                    )}

                    {activeTab === 0 ? (
                        <Grid container spacing={2}>
                            <Grid item xs={12}>
                                <TextField
                                    fullWidth
                                    label="Your Name"
                                    value={playerName}
                                    onChange={(e) => setPlayerName(e.target.value)}
                                    required
                                    autoComplete="off"
                                    sx={{ backgroundColor: 'white' }}
                                />
                            </Grid>
                            <Grid item xs={12} md={4}>
                                <TextField
                                    fullWidth
                                    label="Number of Players"
                                    type="number"
                                    value={playerCount}
                                    onChange={(e) => setPlayerCount(Number(e.target.value))}
                                    inputProps={{ min: 2, max: 5 }}
                                    autoComplete="off"
                                    sx={{ backgroundColor: 'white' }}
                                />
                            </Grid>
                            <Grid item xs={12} md={4}>
                                <TextField
                                    fullWidth
                                    label="Number of Batches"
                                    type="number"
                                    value={maxBatches}
                                    onChange={(e) => setMaxBatches(Number(e.target.value))}
                                    inputProps={{ min: 1, max: 10 }}
                                    autoComplete="off"
                                    sx={{ backgroundColor: 'white' }}
                                />
                            </Grid>
                            <Grid item xs={12} md={4}>
                                <TextField
                                    fullWidth
                                    label="Coins per Batch"
                                    type="number"
                                    value={coinsPerBatch}
                                    onChange={(e) => setCoinsPerBatch(Number(e.target.value))}
                                    inputProps={{ min: 1, max: 10 }}
                                    autoComplete="off"
                                    sx={{ backgroundColor: 'white' }}
                                />
                            </Grid>
                            <Grid item xs={12}>
                                <Button 
                                    variant="contained" 
                                    onClick={handleCreateGame}
                                    disabled={!playerName}
                                    sx={{ mt: 2 }}
                                    size="large"
                                >
                                    Create Game
                                </Button>
                            </Grid>
                        </Grid>
                    ) : (
                        <Grid container spacing={2}>
                            <Grid item xs={12} md={6}>
                                <TextField
                                    fullWidth
                                    label="Game ID"
                                    value={joinGameId}
                                    onChange={(e) => setJoinGameId(e.target.value.toUpperCase())}
                                    autoComplete="off"
                                    sx={{ backgroundColor: 'white' }}
                                />
                            </Grid>
                            <Grid item xs={12} md={6}>
                                <TextField
                                    fullWidth
                                    label="Player Name"
                                    value={playerName}
                                    onChange={(e) => setPlayerName(e.target.value)}
                                    autoComplete="off"
                                    sx={{ backgroundColor: 'white' }}
                                />
                            </Grid>
                            <Grid item xs={12}>
                                <Button 
                                    variant="contained" 
                                    onClick={handleJoinGame}
                                    sx={{ mt: 2 }}
                                    size="large"
                                >
                                    Join Game
                                </Button>
                            </Grid>
                        </Grid>
                    )}
                </Box>
            </Box>
        );
    }

    return (
        <Box sx={{ p: 3, backgroundColor: 'background.default', minHeight: '100vh' }}>
            {error && (
                <Alert severity="error" sx={{ mb: 2 }}>
                    {error}
                </Alert>
            )}

            {game && (
                <>
                    <Box sx={{ 
                        display: 'flex', 
                        justifyContent: 'space-between', 
                        alignItems: 'center',
                        mb: 3
                    }}>
                        <Box>
                            <Typography variant="h4" sx={{ mb: 1, color: 'primary.main' }}>
                                Game ID: {game.id}
                            </Typography>
                            <Typography variant="h5" sx={{ color: 'text.primary' }}>
                                Game Status: {game.status === 'waiting' ? 'Waiting' : game.status === 'in_progress' ? 'In Progress' : 'Completed'}
                            </Typography>
                        </Box>
                        {(game.status === 'in_progress' || game.status === 'completed') && startTime && (
                            <Typography variant="h6" sx={{ color: 'text.secondary' }}>
                                Time: {formatTimeFromMs(game.status === 'completed' && endTime ? endTime - startTime : currentTime)}
                            </Typography>
                        )}
                    </Box>

                    {game.status === 'waiting' && playerId === game.players[0]?.id && (
                        <Button
                            variant="contained"
                            onClick={handleStartGame}
                            sx={{ mb: 3 }}
                        >
                            Start Game
                        </Button>
                    )}

                    <Grid container spacing={3}>
                        {/* Left column: Player cards */}
                        <Grid item xs={12} md={7}>
                            <Stack spacing={3}>
                                {game.players.map(renderPlayer)}
                            </Stack>
                        </Grid>

                        {/* Right column: Results and statistics */}
                        <Grid item xs={12} md={5}>
                            {renderStatistics()}
                        </Grid>
                    </Grid>
                </>
            )}
        </Box>
    );
};
