export enum TaskType {
    CODING = "Coding",
    TESTING = "Testing",
    DOCUMENTATION = "Documentation",
    REVIEW = "Review",
    DESIGN = "Design",
    DEBUGGING = "Debugging",
    DEPLOYMENT = "Deployment",
    OPTIMIZATION = "Optimization",
    RESEARCH = "Research",
    PLANNING = "Planning"
}

export enum TaskStatus {
    PENDING = "Pending",
    IN_PROGRESS = "In Progress",
    COMPLETED = "Completed"
}

export enum PlayerStatus {
    WAITING = "Waiting",
    ACTIVE = "Active",
    IDLE = "Idle"
}

export interface Task {
    id: string;
    type: TaskType;
    description: string;
    status: TaskStatus;
    assigned_to: string | null;
    batch_number: number;
}

export interface Player {
    id: string;
    name: string;
    current_tasks: Task[];
    completed_tasks: Task[];
    status: PlayerStatus;
    order: number;
}

export interface Game {
    id: string;
    players: Player[];
    current_batch: number;
    max_batches: number;
    tasks_per_batch: number;
    tasks: Task[];
    status: string;
    active_player_index: number;
    current_player_order: number;
}

export interface WebSocketMessage {
    type: string;
    data: any;
}
