export enum TaskType {
    HEADS = "Heads",
    TAILS = "Tails"
}

export enum TaskStatus {
    PENDING = "Pending",
    IN_PROGRESS = "In Progress",
    COMPLETED = "Completed"
}

export enum PlayerStatus {
    WAITING = "Waiting",
    ACTIVE = "Active",
    IDLE = "Idle"
}

export enum SubBatchStatus {
    PENDING = "Pending",
    IN_PROGRESS = "In Progress",
    COMPLETED = "Completed",
    PASSED = "Passed"
}

export interface Task {
    id: string;
    type: TaskType;
    description: string;
    batch_number: number;
    sub_batch_number: number;
    status: TaskStatus;
    assigned_to?: string;
}

export interface SubBatch {
    batch_number: number;
    sub_batch_number: number;
    size: number;
    status: SubBatchStatus;
    tasks: Task[];
    completed_by?: string;
    passed_to?: string;
}

export interface Player {
    id: string;
    name: string;
    current_tasks: Task[];
    completed_tasks: Task[];
    status: PlayerStatus;
    order: number;
    current_batch: number;
    current_sub_batches: { [key: number]: Set<number> };  // batch_number -> set of sub_batch_numbers
    completed_sub_batches: { [key: number]: Set<number> };  // batch_number -> set of completed sub_batch_numbers
    task_queue: { [key: number]: Task[] };
    batch_start_time: { [key: number]: number };
    batch_completion_time: { [key: number]: number };
}

export interface Game {
    id: string;
    players: Player[];
    current_batch: number;
    max_batches: number;
    tasks_per_batch: number;
    tasks: Task[];
    sub_batches: { [key: number]: { [key: number]: SubBatch } };  // batch_number -> sub_batch_number -> SubBatch
    status: string;
    active_player_index: number;
    current_player_order: number;
}

export interface WebSocketMessage {
    type: string;
    data: any;
}
