export enum TaskType {
    HEADS = 'HEADS',
    TAILS = 'TAILS'
}

export enum TaskStatus {
    PENDING = 'PENDING',
    IN_PROGRESS = 'IN_PROGRESS',
    COMPLETED = 'COMPLETED'
}

export enum PlayerStatus {
    WAITING = 'WAITING',
    IDLE = 'IDLE',
    ACTIVE = 'ACTIVE',
    COMPLETED = 'COMPLETED'
}

export interface Task {
    id: string;
    type: TaskType;
    status: TaskStatus;
    batch_number: number;
    assigned_to?: string;
}

export interface Player {
    id: string;
    name: string;
    status: PlayerStatus;
    current_batch: number;
    batch_start_time?: { [key: number]: number };
    batch_completion_time?: { [key: number]: number };
}

export interface Game {
    id: string;
    status: 'waiting' | 'in_progress' | 'completed';
    players: Player[];
    tasks: Task[];
    max_batches: number;
    max_players: number;
}

export interface WebSocketMessage {
    type: 'game_update' | 'complete_task' | 'start_game';
    game?: Game;
    task_id?: string;
}
