import React, { useState, useEffect } from 'react';
import {
    Box,
    Button,
    Card,
    CardContent,
    Typography,
    Grid,
    TextField,
    Dialog,
    DialogTitle,
    DialogContent,
    DialogActions,
    Tabs,
    Tab,
    Alert
} from '@mui/material';
import { Game as GameType, Player, Task, TaskStatus } from '../types';
import { gameService, WebSocketService } from '../services/gameService';

interface GameProps {
    gameId?: string;
}

export const Game: React.FC<GameProps> = ({ gameId: initialGameId }) => {
    const [gameId, setGameId] = useState<string | undefined>(initialGameId);
    const [game, setGame] = useState<GameType | null>(null);
    const [playerName, setPlayerName] = useState('');
    const [playerId, setPlayerId] = useState<string | null>(null);
    const [wsService, setWsService] = useState<WebSocketService | null>(null);
    const [showJoinDialog, setShowJoinDialog] = useState(!initialGameId);
    const [playerCount, setPlayerCount] = useState(2);
    const [maxBatches, setMaxBatches] = useState(3);
    const [tasksPerBatch, setTasksPerBatch] = useState(2);
    const [joinGameId, setJoinGameId] = useState('');
    const [error, setError] = useState<string | null>(null);
    const [activeTab, setActiveTab] = useState(0);

    useEffect(() => {
        if (gameId && playerId) {
            const service = new WebSocketService(gameId, playerId, (updatedGame) => {
                setGame(updatedGame);
            });
            service.connect();
            setWsService(service);

            return () => {
                service.disconnect();
            };
        }
    }, [gameId, playerId]);

    const handleCreateGame = async () => {
        try {
            const newGameId = await gameService.createGame(playerCount, maxBatches, tasksPerBatch);
            setGameId(newGameId);
            setError(null);
        } catch (error) {
            setError('创建游戏失败，请重试');
            console.error('Error creating game:', error);
        }
    };

    const handleJoinGame = async () => {
        if (!playerName) {
            setError('请输入玩家名称');
            return;
        }

        const targetGameId = gameId || joinGameId;
        if (!targetGameId) {
            setError('请输入游戏ID');
            return;
        }

        try {
            // 检查游戏是否存在
            const exists = await gameService.checkGameExists(targetGameId);
            if (!exists) {
                setError('游戏不存在');
                return;
            }

            const newPlayerId = await gameService.joinGame(targetGameId, playerName);
            setPlayerId(newPlayerId);
            setGameId(targetGameId);
            setShowJoinDialog(false);
            const gameState = await gameService.getGameState(targetGameId);
            setGame(gameState);
            setError(null);
        } catch (error) {
            setError('加入游戏失败，请重试');
            console.error('Error joining game:', error);
        }
    };

    const handleStartGame = async () => {
        if (!gameId) return;

        try {
            await gameService.startGame(gameId);
            setError(null);
        } catch (error) {
            setError('开始游戏失败，请重试');
            console.error('Error starting game:', error);
        }
    };

    const handleCompleteTask = (taskId: string) => {
        if (wsService) {
            wsService.completeTask(taskId);
        }
    };

    const renderTask = (task: Task) => (
        <Card key={task.id} sx={{ mb: 2 }}>
            <CardContent>
                <Typography variant="h6" color="primary">{task.type}</Typography>
                <Typography variant="body1">{task.description}</Typography>
                <Typography color="textSecondary" sx={{ mt: 1 }}>
                    批次: {task.batch_number} | 状态: {task.status}
                </Typography>
                {task.status === TaskStatus.PENDING && task.assigned_to === playerId && (
                    <Button
                        variant="contained"
                        color="primary"
                        onClick={() => handleCompleteTask(task.id)}
                        sx={{ mt: 1 }}
                    >
                        完成任务
                    </Button>
                )}
            </CardContent>
        </Card>
    );

    const renderPlayer = (player: Player) => (
        <Grid item xs={12} md={6} key={player.id}>
            <Card>
                <CardContent>
                    <Typography variant="h6" color="primary">{player.name}</Typography>
                    <Typography color="textSecondary">
                        当前任务: {player.current_tasks.length}
                    </Typography>
                    <Typography color="textSecondary" sx={{ mb: 2 }}>
                        已完成任务: {player.completed_tasks.length}
                    </Typography>
                    {player.current_tasks.map(renderTask)}
                </CardContent>
            </Card>
        </Grid>
    );

    if (!gameId || !playerId) {
        return (
            <Box sx={{ p: 3 }}>
                <Tabs value={activeTab} onChange={(_, newValue) => setActiveTab(newValue)} sx={{ mb: 3 }}>
                    <Tab label="创建游戏" />
                    <Tab label="加入游戏" />
                </Tabs>

                {error && (
                    <Alert severity="error" sx={{ mb: 2 }}>
                        {error}
                    </Alert>
                )}

                {activeTab === 0 ? (
                    <Grid container spacing={2}>
                        <Grid item xs={12} md={4}>
                            <TextField
                                fullWidth
                                label="玩家数量"
                                type="number"
                                value={playerCount}
                                onChange={(e) => setPlayerCount(Number(e.target.value))}
                                inputProps={{ min: 2, max: 10 }}
                            />
                        </Grid>
                        <Grid item xs={12} md={4}>
                            <TextField
                                fullWidth
                                label="批次数量"
                                type="number"
                                value={maxBatches}
                                onChange={(e) => setMaxBatches(Number(e.target.value))}
                                inputProps={{ min: 1, max: 10 }}
                            />
                        </Grid>
                        <Grid item xs={12} md={4}>
                            <TextField
                                fullWidth
                                label="每批次任务数"
                                type="number"
                                value={tasksPerBatch}
                                onChange={(e) => setTasksPerBatch(Number(e.target.value))}
                                inputProps={{ min: 1, max: 10 }}
                            />
                        </Grid>
                        <Grid item xs={12}>
                            <Button variant="contained" onClick={handleCreateGame}>
                                创建游戏
                            </Button>
                        </Grid>
                    </Grid>
                ) : (
                    <Grid container spacing={2}>
                        <Grid item xs={12} md={6}>
                            <TextField
                                fullWidth
                                label="游戏ID"
                                value={joinGameId}
                                onChange={(e) => setJoinGameId(e.target.value.toUpperCase())}
                            />
                        </Grid>
                        <Grid item xs={12} md={6}>
                            <TextField
                                fullWidth
                                label="玩家名称"
                                value={playerName}
                                onChange={(e) => setPlayerName(e.target.value)}
                            />
                        </Grid>
                        <Grid item xs={12}>
                            <Button variant="contained" onClick={handleJoinGame}>
                                加入游戏
                            </Button>
                        </Grid>
                    </Grid>
                )}

                {gameId && !playerId && (
                    <Dialog open={true} onClose={() => setShowJoinDialog(false)}>
                        <DialogTitle>加入游戏</DialogTitle>
                        <DialogContent>
                            <TextField
                                autoFocus
                                margin="dense"
                                label="玩家名称"
                                fullWidth
                                value={playerName}
                                onChange={(e) => setPlayerName(e.target.value)}
                            />
                        </DialogContent>
                        <DialogActions>
                            <Button onClick={handleJoinGame}>加入</Button>
                        </DialogActions>
                    </Dialog>
                )}
            </Box>
        );
    }

    return (
        <Box sx={{ p: 3 }}>
            {error && (
                <Alert severity="error" sx={{ mb: 2 }}>
                    {error}
                </Alert>
            )}

            {game && (
                <>
                    <Typography variant="h4" sx={{ mb: 1 }}>
                        游戏ID: {game.id}
                    </Typography>
                    <Typography variant="h5" sx={{ mb: 3 }}>
                        游戏状态: {game.status === 'waiting' ? '等待开始' : game.status === 'in_progress' ? '进行中' : '已完成'}
                    </Typography>
                    <Typography variant="h6" sx={{ mb: 2 }}>
                        当前批次: {game.current_batch} / {game.max_batches}
                    </Typography>

                    {game.status === 'waiting' && playerId === game.players[0]?.id && (
                        <Button
                            variant="contained"
                            onClick={handleStartGame}
                            sx={{ mb: 3 }}
                        >
                            开始游戏
                        </Button>
                    )}

                    <Grid container spacing={3}>
                        {game.players.map(renderPlayer)}
                    </Grid>
                </>
            )}
        </Box>
    );
};
