export enum TaskType {
    HEADS = "HEADS",
    TAILS = "TAILS"
}

export enum TaskStatus {
    PENDING = "PENDING",
    IN_PROGRESS = "IN_PROGRESS",
    COMPLETED = "COMPLETED"
}

export enum PlayerStatus {
    IDLE = "IDLE",
    ACTIVE = "ACTIVE",
    WAITING = "WAITING"
}

export interface Task {
    id: string;
    type: TaskType;
    description: string;
    batch_number: number;
    status: TaskStatus;
    assigned_to?: string;
}

export interface Player {
    id: string;
    name: string;
    order: number;
    status: PlayerStatus;
    current_batch: number;
    current_tasks: Task[];
    completed_tasks: Task[];
    task_queue: { [key: number]: Task[] };
    batch_start_time: { [key: number]: number };
    batch_completion_time: { [key: number]: number };
}

export interface Game {
    id: string;
    players: Player[];
    max_batches: number;
    tasks_per_batch: number;
    tasks: Task[];
    status: "waiting" | "in_progress" | "completed";
    current_batch?: number;
}

export interface WebSocketMessage {
    type: string;
    data: any;
}
