// 任务类型枚举
export enum TaskType {
    HEADS = 'HEADS',
    TAILS = 'TAILS'
}

// 任务状态枚举
export enum TaskStatus {
    PENDING = 'PENDING',
    IN_PROGRESS = 'IN_PROGRESS',
    COMPLETED = 'COMPLETED'
}

// 玩家状态枚举
export enum PlayerStatus {
    WAITING = 'WAITING',
    PLAYING = 'PLAYING',
    COMPLETED = 'COMPLETED'
}

// 任务接口
export interface Task {
    id: string;
    type: TaskType;
    status: TaskStatus;
    batch_number: number;
    assigned_to: string;
    started_at?: number;
}

// 玩家接口
export interface Player {
    id: string;
    name: string;
    status: PlayerStatus;
    current_batch: number;
    batch_start_time?: { [key: number]: number };
    batch_completion_time?: { [key: number]: number };
}

// 游戏接口
export interface Game {
    id: string;
    status: 'waiting' | 'in_progress' | 'completed';
    players: Player[];
    tasks: Task[];
    max_batches: number;
    current_batch: number;
}

// WebSocket消息接口
export interface WebSocketMessage {
    type: 'game_update' | 'error';
    data: Game | string;
}
