import React, { useState, useEffect } from 'react';
import {
    Box,
    Button,
    Card,
    CardContent,
    Typography,
    Grid,
    TextField,
    Dialog,
    DialogTitle,
    DialogContent,
    DialogActions,
    Tabs,
    Tab,
    Alert,
    Chip
} from '@mui/material';
import { Game as GameType, Player, Task, TaskStatus, PlayerStatus } from '../types';
import { gameService, WebSocketService } from '../services/gameService';

interface GameProps {
    gameId?: string;
}

export const Game: React.FC<GameProps> = ({ gameId: initialGameId }) => {
    const [gameId, setGameId] = useState<string | undefined>(initialGameId);
    const [game, setGame] = useState<GameType | null>(null);
    const [playerName, setPlayerName] = useState('');
    const [playerId, setPlayerId] = useState<string | null>(null);
    const [wsService, setWsService] = useState<WebSocketService | null>(null);
    const [showJoinDialog, setShowJoinDialog] = useState(!initialGameId);
    const [playerCount, setPlayerCount] = useState(2);
    const [maxBatches, setMaxBatches] = useState(3);
    const [tasksPerBatch, setTasksPerBatch] = useState(2);
    const [joinGameId, setJoinGameId] = useState('');
    const [error, setError] = useState<string | null>(null);
    const [activeTab, setActiveTab] = useState(0);

    useEffect(() => {
        if (gameId && playerId) {
            const service = new WebSocketService(gameId, playerId, (updatedGame) => {
                setGame(updatedGame);
            }, (message) => {
                // Handle turn notification
                if (message.type === 'your_turn') {
                    setError(message.data);
                }
            });
            service.connect();
            setWsService(service);

            return () => {
                service.disconnect();
            };
        }
    }, [gameId, playerId]);

    const handleCreateGame = async () => {
        if (!playerName) {
            setError('Please enter your name first');
            return;
        }

        try {
            const newGameId = await gameService.createGame(playerCount, maxBatches, tasksPerBatch);
            setGameId(newGameId);

            // Automatically join the game as the creator
            const newPlayerId = await gameService.joinGame(newGameId, playerName);
            setPlayerId(newPlayerId);

            // Get initial game state
            const gameState = await gameService.getGameState(newGameId);
            setGame(gameState);

            setError(null);
        } catch (error) {
            setError('Failed to create game. Please try again.');
            console.error('Error creating game:', error);
        }
    };

    const handleJoinGame = async () => {
        if (!playerName) {
            setError('Please enter player name');
            return;
        }

        const targetGameId = gameId || joinGameId;
        if (!targetGameId) {
            setError('Please enter game ID');
            return;
        }

        try {
            const exists = await gameService.checkGameExists(targetGameId);
            if (!exists) {
                setError('Game not found');
                return;
            }

            const newPlayerId = await gameService.joinGame(targetGameId, playerName);
            setPlayerId(newPlayerId);
            setGameId(targetGameId);
            setShowJoinDialog(false);
            const gameState = await gameService.getGameState(targetGameId);
            setGame(gameState);
            setError(null);
        } catch (error) {
            setError('Failed to join game. Please try again.');
            console.error('Error joining game:', error);
        }
    };

    const handleStartGame = async () => {
        if (!gameId) return;

        try {
            await gameService.startGame(gameId);
            setError(null);
        } catch (error) {
            setError('Failed to start game. Please try again.');
            console.error('Error starting game:', error);
        }
    };

    const handleCompleteTask = (taskId: string) => {
        if (wsService) {
            wsService.completeTask(taskId);
        }
    };

    const getPlayerStatusColor = (status: PlayerStatus) => {
        switch (status) {
            case PlayerStatus.ACTIVE:
                return 'success';
            case PlayerStatus.WAITING:
                return 'warning';
            case PlayerStatus.IDLE:
                return 'default';
            default:
                return 'default';
        }
    };

    const renderTask = (task: Task) => (
        <Card key={task.id} sx={{ mb: 2 }}>
            <CardContent>
                <Typography variant="h6" color="primary">{task.type}</Typography>
                <Typography variant="body1">{task.description}</Typography>
                <Typography color="textSecondary" sx={{ mt: 1 }}>
                    Batch: {task.batch_number} | Status: {task.status}
                </Typography>
                {task.status === TaskStatus.PENDING && task.assigned_to === playerId && (
                    <Button
                        variant="contained"
                        color="primary"
                        onClick={() => handleCompleteTask(task.id)}
                        sx={{ mt: 1 }}
                    >
                        Complete Task
                    </Button>
                )}
            </CardContent>
        </Card>
    );

    const renderPlayer = (player: Player) => (
        <Grid item xs={12} md={6} key={player.id}>
            <Card>
                <CardContent>
                    <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                        <Typography variant="h6" color="primary" sx={{ mr: 2 }}>{player.name}</Typography>
                        <Chip 
                            label={player.status} 
                            color={getPlayerStatusColor(player.status)}
                            size="small"
                        />
                    </Box>
                    <Typography color="textSecondary">
                        Current Tasks: {player.current_tasks.length}
                    </Typography>
                    <Typography color="textSecondary" sx={{ mb: 2 }}>
                        Completed Tasks: {player.completed_tasks.length}
                    </Typography>
                    {player.current_tasks.map(renderTask)}
                </CardContent>
            </Card>
        </Grid>
    );

    if (!gameId || !playerId) {
        return (
            <Box sx={{ p: 3 }}>
                <Tabs value={activeTab} onChange={(_, newValue) => setActiveTab(newValue)} sx={{ mb: 3 }}>
                    <Tab label="Create Game" />
                    <Tab label="Join Game" />
                </Tabs>

                {error && (
                    <Alert severity="error" sx={{ mb: 2 }}>
                        {error}
                    </Alert>
                )}

                {activeTab === 0 ? (
                    <Grid container spacing={2}>
                        <Grid item xs={12}>
                            <TextField
                                fullWidth
                                label="Your Name"
                                value={playerName}
                                onChange={(e) => setPlayerName(e.target.value)}
                                required
                            />
                        </Grid>
                        <Grid item xs={12} md={4}>
                            <TextField
                                fullWidth
                                label="Number of Players"
                                type="number"
                                value={playerCount}
                                onChange={(e) => setPlayerCount(Number(e.target.value))}
                                inputProps={{ min: 2, max: 10 }}
                            />
                        </Grid>
                        <Grid item xs={12} md={4}>
                            <TextField
                                fullWidth
                                label="Number of Batches"
                                type="number"
                                value={maxBatches}
                                onChange={(e) => setMaxBatches(Number(e.target.value))}
                                inputProps={{ min: 1, max: 10 }}
                            />
                        </Grid>
                        <Grid item xs={12} md={4}>
                            <TextField
                                fullWidth
                                label="Tasks per Batch"
                                type="number"
                                value={tasksPerBatch}
                                onChange={(e) => setTasksPerBatch(Number(e.target.value))}
                                inputProps={{ min: 1, max: 10 }}
                            />
                        </Grid>
                        <Grid item xs={12}>
                            <Button 
                                variant="contained" 
                                onClick={handleCreateGame}
                                disabled={!playerName}
                            >
                                Create Game
                            </Button>
                        </Grid>
                    </Grid>
                ) : (
                    <Grid container spacing={2}>
                        <Grid item xs={12} md={6}>
                            <TextField
                                fullWidth
                                label="Game ID"
                                value={joinGameId}
                                onChange={(e) => setJoinGameId(e.target.value.toUpperCase())}
                            />
                        </Grid>
                        <Grid item xs={12} md={6}>
                            <TextField
                                fullWidth
                                label="Player Name"
                                value={playerName}
                                onChange={(e) => setPlayerName(e.target.value)}
                            />
                        </Grid>
                        <Grid item xs={12}>
                            <Button variant="contained" onClick={handleJoinGame}>
                                Join Game
                            </Button>
                        </Grid>
                    </Grid>
                )}
            </Box>
        );
    }

    return (
        <Box sx={{ p: 3 }}>
            {error && (
                <Alert severity="error" sx={{ mb: 2 }}>
                    {error}
                </Alert>
            )}

            {game && (
                <>
                    <Typography variant="h4" sx={{ mb: 1 }}>
                        Game ID: {game.id}
                    </Typography>
                    <Typography variant="h5" sx={{ mb: 3 }}>
                        Game Status: {game.status === 'waiting' ? 'Waiting' : game.status === 'in_progress' ? 'In Progress' : 'Completed'}
                    </Typography>
                    <Typography variant="h6" sx={{ mb: 2 }}>
                        Current Batch: {game.current_batch} / {game.max_batches}
                    </Typography>

                    {game.status === 'waiting' && playerId === game.players[0]?.id && (
                        <Button
                            variant="contained"
                            onClick={handleStartGame}
                            sx={{ mb: 3 }}
                        >
                            Start Game
                        </Button>
                    )}

                    <Grid container spacing={3}>
                        {game.players.map(renderPlayer)}
                    </Grid>
                </>
            )}
        </Box>
    );
};
