export enum TaskType {
    HEADS = 'HEADS',
    TAILS = 'TAILS'
}

export enum TaskStatus {
    PENDING = 'PENDING',
    IN_PROGRESS = 'IN_PROGRESS',
    COMPLETED = 'COMPLETED'
}

export enum PlayerStatus {
    IDLE = 'IDLE',
    ACTIVE = 'ACTIVE',
    WAITING = 'WAITING'
}

export interface Task {
    id: string;
    type: TaskType;
    status: TaskStatus;
    batch_number: number;
    batch_id: number;
    assigned_to: string;
}

export interface Player {
    id: string;
    name: string;
    status: PlayerStatus;
    current_tasks: Task[];
    completed_tasks: Task[];
    current_batch: number;
    order: number;
}

export interface Game {
    id: string;
    status: 'waiting' | 'in_progress' | 'completed';
    players: Player[];
    tasks: Task[];
    max_batches: number;
    coins_per_batch: number;
    max_players: number;
}

export interface WebSocketMessage {
    type: string;
    game_state?: Game;
    data?: any;
}

export interface Player {
    id: string;
    name: string;
    status: PlayerStatus;
    tasks: string[];
}
