import React, { useState, useEffect } from 'react';
import {
    Box,
    Button,
    Card,
    CardContent,
    Typography,
    Grid,
    TextField,
    Dialog,
    DialogTitle,
    DialogContent,
    DialogActions,
    Tabs,
    Tab,
    Alert,
    Chip,
    keyframes
} from '@mui/material';
import { Game as GameType, Player, Task, TaskStatus, PlayerStatus, TaskType } from '../types';
import { gameService, WebSocketService } from '../services/gameService';

interface GameProps {
    gameId?: string;
}

export const Game: React.FC<GameProps> = ({ gameId: initialGameId }) => {
    const [gameId, setGameId] = useState<string | undefined>(initialGameId);
    const [game, setGame] = useState<GameType | null>(null);
    const [playerName, setPlayerName] = useState('');
    const [playerId, setPlayerId] = useState<string | null>(null);
    const [wsService, setWsService] = useState<WebSocketService | null>(null);
    const [showJoinDialog, setShowJoinDialog] = useState(!initialGameId);
    const [playerCount, setPlayerCount] = useState(2);
    const [maxBatches, setMaxBatches] = useState(3);
    const [coinsPerBatch, setCoinsPerBatch] = useState(2);
    const [joinGameId, setJoinGameId] = useState('');
    const [error, setError] = useState<string | null>(null);
    const [activeTab, setActiveTab] = useState(0);
    const [startTime, setStartTime] = useState<number | null>(null);
    const [endTime, setEndTime] = useState<number | null>(null);
    const [showCompletionDialog, setShowCompletionDialog] = useState(false);

    useEffect(() => {
        if (gameId && playerId) {
            const service = new WebSocketService(gameId, playerId, (updatedGame) => {
                console.log('Game state updated:', updatedGame);
                setGame(updatedGame);
            });
            service.connect();
            setWsService(service);

            // Cleanup function
            return () => {
                service.disconnect();
            };
        }
    }, [gameId, playerId]);

    useEffect(() => {
        if (game?.status === 'in_progress' && !startTime) {
            setStartTime(Date.now());
        }
    }, [game?.status, startTime]);

    const handleCreateGame = async () => {
        if (!playerName) {
            setError('Please enter your name first');
            return;
        }

        try {
            const newGameId = await gameService.createGame(playerCount, maxBatches, coinsPerBatch);
            setGameId(newGameId);

            // Automatically join the game as the creator
            const newPlayerId = await gameService.joinGame(newGameId, playerName);
            setPlayerId(newPlayerId);

            // Get initial game state
            const gameState = await gameService.getGameState(newGameId);
            setGame(gameState);

            // Reset all input fields
            setPlayerName('');
            setPlayerCount(2);
            setMaxBatches(3);
            setCoinsPerBatch(2);
            setJoinGameId('');
            setError(null);
        } catch (error) {
            setError('Failed to create game. Please try again.');
            console.error('Error creating game:', error);
        }
    };

    const handleJoinGame = async () => {
        if (!playerName) {
            setError('Please enter player name');
            return;
        }

        const targetGameId = gameId || joinGameId;
        if (!targetGameId) {
            setError('Please enter game ID');
            return;
        }

        try {
            const exists = await gameService.checkGameExists(targetGameId);
            if (!exists) {
                setError('Game not found');
                return;
            }

            const newPlayerId = await gameService.joinGame(targetGameId, playerName);
            setPlayerId(newPlayerId);
            setGameId(targetGameId);
            setShowJoinDialog(false);
            const gameState = await gameService.getGameState(targetGameId);
            setGame(gameState);

            // Reset all input fields
            setPlayerName('');
            setPlayerCount(2);
            setMaxBatches(3);
            setCoinsPerBatch(2);
            setJoinGameId('');
            setError(null);
        } catch (error) {
            setError('Failed to join game. Please try again.');
            console.error('Error joining game:', error);
        }
    };

    const handleStartGame = async () => {
        if (!gameId) return;

        try {
            await gameService.startGame(gameId);
            setError(null);
        } catch (error) {
            setError('Failed to start game. Please try again.');
            console.error('Error starting game:', error);
        }
    };

    const flipAnimation = keyframes`
        0% {
            transform: rotateY(0deg);
        }
        100% {
            transform: rotateY(1800deg);
        }
    `;

    const renderCoin = (type: TaskType, isFlipping: boolean, isSmall: boolean = false, onClick?: () => void) => (
        <Box
            sx={{
                width: isSmall ? 40 : 80,
                height: isSmall ? 40 : 80,
                borderRadius: '50%',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                backgroundColor: type === TaskType.HEADS ? '#FFD700' : '#DAA520',
                border: `${isSmall ? 2 : 4}px solid #B8860B`,
                color: '#704214',
                fontWeight: 'bold',
                fontSize: isSmall ? '0.9rem' : '1.2rem',
                animation: isFlipping ? `${flipAnimation} 1s ease-in-out` : 'none',
                perspective: 1000,
                mb: isSmall ? 0 : 2,
                position: 'relative',
                cursor: onClick ? 'pointer' : 'default',
                transition: 'transform 0.2s ease-in-out, box-shadow 0.2s ease-in-out',
                '&:hover': onClick ? {
                    transform: 'scale(1.05)',
                    boxShadow: '0 4px 8px rgba(0,0,0,0.2)'
                } : {},
                '&:active': onClick ? {
                    transform: 'scale(0.95)'
                } : {},
                '&::after': {
                    content: '""',
                    position: 'absolute',
                    top: 0,
                    left: 0,
                    right: 0,
                    bottom: 0,
                    borderRadius: '50%',
                    background: 'linear-gradient(135deg, rgba(255,255,255,0.3) 0%, rgba(255,255,255,0) 50%, rgba(0,0,0,0.1) 100%)',
                    pointerEvents: 'none'
                }
            }}
            onClick={onClick}
        >
            {type === TaskType.HEADS ? 'H' : 'T'}
        </Box>
    );

    const [flippingCoins, setFlippingCoins] = useState<{[key: string]: boolean}>({});

    const handleCompleteTask = (taskId: string) => {
        if (wsService) {
            setFlippingCoins(prev => ({ ...prev, [taskId]: true }));
            setTimeout(() => {
                wsService.completeTask(taskId);
                setFlippingCoins(prev => ({ ...prev, [taskId]: false }));
            }, 1000);
        }
    };

    const getPlayerStatusColor = (status: PlayerStatus) => {
        switch (status) {
            case PlayerStatus.ACTIVE:
                return 'success';
            case PlayerStatus.WAITING:
                return 'warning';
            case PlayerStatus.IDLE:
                return 'default';
            default:
                return 'default';
        }
    };

    const getTaskStatusColor = (status: TaskStatus) => {
        switch (status) {
            case TaskStatus.COMPLETED:
                return '#4caf50';
            case TaskStatus.IN_PROGRESS:
                return '#ff9800';
            case TaskStatus.PENDING:
                return '#9e9e9e';
            default:
                return '#9e9e9e';
        }
    };

    const renderTask = (task: Task, playerName: string) => (
        <Card 
            key={`${task.id}-${playerName}-${task.batch_number}`} 
            sx={{ 
                mb: 2,
                backgroundColor: 'background.paper',
                position: 'relative',
                overflow: 'visible'
            }}
        >
            <CardContent sx={{ 
                pb: '16px !important',
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center'
            }}>
                {renderCoin(
                    task.type, 
                    flippingCoins[task.id] || false,
                    false,
                    task.status === TaskStatus.PENDING && task.assigned_to === playerId && !flippingCoins[task.id]
                        ? () => handleCompleteTask(task.id)
                        : undefined
                )}
                <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', width: '100%', mb: 1 }}>
                    <Typography variant="h6" sx={{ color: 'primary.main' }}>
                        {task.type === TaskType.HEADS ? 'Flip to Heads' : 'Flip to Tails'}
                    </Typography>
                    <Chip
                        label={task.status}
                        size="small"
                        sx={{
                            backgroundColor: getTaskStatusColor(task.status),
                            color: 'white',
                            fontWeight: 500,
                        }}
                    />
                </Box>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', width: '100%' }}>
                    <Typography variant="body2" color="textSecondary">
                        Batch: {task.batch_number}
                    </Typography>
                </Box>
            </CardContent>
        </Card>
    );

    const renderPlayer = (player: Player) => {
        const currentBatchTarget = player.current_tasks.length > 0 
            ? player.current_tasks[0].type 
            : player.completed_tasks.find(t => t.batch_number === player.current_batch)?.type;

        return (
            <Grid item xs={12} md={6} key={`player-${player.id}`}>
                <Card sx={{ height: '100%' }}>
                    <CardContent>
                        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
                            <Typography variant="h6" sx={{ color: 'primary.main' }}>{player.name}</Typography>
                            <Chip
                                label={player.status}
                                size="small"
                                color={getPlayerStatusColor(player.status)}
                                sx={{ fontWeight: 500 }}
                            />
                        </Box>
                        <Grid container spacing={2} sx={{ mb: 3 }}>
                            <Grid item xs={12}>
                                <Box sx={{ 
                                    display: 'flex', 
                                    alignItems: 'center', 
                                    gap: 2, 
                                    mb: 2,
                                    backgroundColor: 'rgba(33, 150, 243, 0.1)',
                                    p: 2,
                                    borderRadius: 2
                                }}>
                                    <Typography variant="body1" sx={{ fontWeight: 500 }}>
                                        Current Batch: {player.current_batch} / {game?.max_batches}
                                    </Typography>
                                    {currentBatchTarget && (
                                        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                                            <Typography variant="body1" sx={{ fontWeight: 500 }}>
                                                Target:
                                            </Typography>
                                            {renderCoin(currentBatchTarget, false, true)}
                                        </Box>
                                    )}
                                </Box>
                            </Grid>
                            <Grid item xs={6}>
                                <Typography variant="body2" color="textSecondary">Current Tasks</Typography>
                                <Typography variant="h6">{player.current_tasks.length}</Typography>
                            </Grid>
                            <Grid item xs={6}>
                                <Typography variant="body2" color="textSecondary">Completed</Typography>
                                <Typography variant="h6">{player.completed_tasks.length}</Typography>
                            </Grid>
                        </Grid>
                        <Box sx={{ mt: 3 }}>
                            <Typography variant="subtitle1" sx={{ color: 'primary.main', fontWeight: 500, mb: 2 }}>
                                Current Tasks
                            </Typography>
                            {player.current_tasks.map(task => renderTask(task, player.name))}
                        </Box>
                        {player.completed_tasks.length > 0 && game && player.order === game.players.length - 1 && (
                            <Box sx={{ mt: 3 }}>
                                <Typography variant="subtitle1" sx={{ color: 'primary.main', fontWeight: 500, mb: 2 }}>
                                    Completed Tasks
                                </Typography>
                                {player.completed_tasks.map(task => renderTask(task, player.name))}
                            </Box>
                        )}
                    </CardContent>
                </Card>
            </Grid>
        );
    };

    const handleTabChange = (_: React.SyntheticEvent, newValue: number) => {
        // Reset all fields when switching tabs
        setActiveTab(newValue);
        setPlayerName('');
        setPlayerCount(2);
        setMaxBatches(3);
        setCoinsPerBatch(2);
        setJoinGameId('');
        setError(null);
    };

    const handleGameComplete = () => {
        setEndTime(Date.now());
        setShowCompletionDialog(true);
    };

    const formatTime = (milliseconds: number) => {
        const seconds = Math.floor(milliseconds / 1000);
        const minutes = Math.floor(seconds / 60);
        const remainingSeconds = seconds % 60;
        return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
    };

    const renderCompletionDialog = () => {
        if (!startTime || !endTime) return null;
        const totalTime = endTime - startTime;

        return (
            <Dialog 
                open={showCompletionDialog} 
                onClose={() => setShowCompletionDialog(false)}
                PaperProps={{
                    sx: {
                        borderRadius: 2,
                        p: 2
                    }
                }}
            >
                <DialogTitle sx={{ textAlign: 'center', color: 'primary.main' }}>
                    Game Completed! 
                </DialogTitle>
                <DialogContent>
                    <Box sx={{ 
                        display: 'flex', 
                        flexDirection: 'column', 
                        alignItems: 'center',
                        gap: 2,
                        py: 2
                    }}>
                        <Typography variant="h6">
                            Total Time: {formatTime(totalTime)}
                        </Typography>
                        <Typography variant="body1" color="text.secondary">
                            All players have successfully completed their tasks!
                        </Typography>
                    </Box>
                </DialogContent>
                <DialogActions sx={{ justifyContent: 'center' }}>
                    <Button 
                        variant="contained" 
                        onClick={() => {
                            setShowCompletionDialog(false);
                            window.location.reload(); // Reset the game
                        }}
                    >
                        Start New Game
                    </Button>
                </DialogActions>
            </Dialog>
        );
    };

    if (!gameId || !playerId) {
        return (
            <Box sx={{ p: 3, backgroundColor: 'background.default', minHeight: '100vh' }}>
                <Tabs 
                    value={activeTab} 
                    onChange={handleTabChange} 
                    sx={{ 
                        mb: 3,
                        borderBottom: 1,
                        borderColor: 'divider'
                    }}
                >
                    <Tab label="Create Game" />
                    <Tab label="Join Game" />
                </Tabs>

                {error && (
                    <Alert severity="error" sx={{ mb: 2 }}>
                        {error}
                    </Alert>
                )}

                {activeTab === 0 ? (
                    <Grid container spacing={2}>
                        <Grid item xs={12}>
                            <TextField
                                fullWidth
                                label="Your Name"
                                value={playerName}
                                onChange={(e) => setPlayerName(e.target.value)}
                                required
                                autoComplete="off"
                                sx={{ backgroundColor: 'white' }}
                            />
                        </Grid>
                        <Grid item xs={12} md={4}>
                            <TextField
                                fullWidth
                                label="Number of Players"
                                type="number"
                                value={playerCount}
                                onChange={(e) => setPlayerCount(Number(e.target.value))}
                                inputProps={{ min: 2, max: 10 }}
                                autoComplete="off"
                                sx={{ backgroundColor: 'white' }}
                            />
                        </Grid>
                        <Grid item xs={12} md={4}>
                            <TextField
                                fullWidth
                                label="Number of Batches"
                                type="number"
                                value={maxBatches}
                                onChange={(e) => setMaxBatches(Number(e.target.value))}
                                inputProps={{ min: 1, max: 10 }}
                                autoComplete="off"
                                sx={{ backgroundColor: 'white' }}
                            />
                        </Grid>
                        <Grid item xs={12} md={4}>
                            <TextField
                                fullWidth
                                label="Coins per Batch"
                                type="number"
                                value={coinsPerBatch}
                                onChange={(e) => setCoinsPerBatch(Number(e.target.value))}
                                inputProps={{ min: 1, max: 10 }}
                                autoComplete="off"
                                sx={{ backgroundColor: 'white' }}
                            />
                        </Grid>
                        <Grid item xs={12}>
                            <Button 
                                variant="contained" 
                                onClick={handleCreateGame}
                                disabled={!playerName}
                                sx={{ mt: 2 }}
                            >
                                Create Game
                            </Button>
                        </Grid>
                    </Grid>
                ) : (
                    <Grid container spacing={2}>
                        <Grid item xs={12} md={6}>
                            <TextField
                                fullWidth
                                label="Game ID"
                                value={joinGameId}
                                onChange={(e) => setJoinGameId(e.target.value.toUpperCase())}
                                autoComplete="off"
                                sx={{ backgroundColor: 'white' }}
                            />
                        </Grid>
                        <Grid item xs={12} md={6}>
                            <TextField
                                fullWidth
                                label="Player Name"
                                value={playerName}
                                onChange={(e) => setPlayerName(e.target.value)}
                                autoComplete="off"
                                sx={{ backgroundColor: 'white' }}
                            />
                        </Grid>
                        <Grid item xs={12}>
                            <Button 
                                variant="contained" 
                                onClick={handleJoinGame}
                                sx={{ mt: 2 }}
                            >
                                Join Game
                            </Button>
                        </Grid>
                    </Grid>
                )}
            </Box>
        );
    }

    return (
        <Box sx={{ p: 3, backgroundColor: 'background.default', minHeight: '100vh' }}>
            {error && (
                <Alert severity="error" sx={{ mb: 2 }}>
                    {error}
                </Alert>
            )}

            {game && (
                <>
                    <Box sx={{ 
                        display: 'flex', 
                        justifyContent: 'space-between', 
                        alignItems: 'center',
                        mb: 3
                    }}>
                        <Box>
                            <Typography variant="h4" sx={{ mb: 1, color: 'primary.main' }}>
                                Game ID: {game.id}
                            </Typography>
                            <Typography variant="h5" sx={{ color: 'text.primary' }}>
                                Game Status: {game.status === 'waiting' ? 'Waiting' : game.status === 'in_progress' ? 'In Progress' : 'Completed'}
                            </Typography>
                        </Box>
                        {game.status === 'in_progress' && startTime && (
                            <Typography variant="h6" sx={{ color: 'text.secondary' }}>
                                Time: {formatTime(Date.now() - startTime)}
                            </Typography>
                        )}
                    </Box>

                    {game.status === 'waiting' && playerId === game.players[0]?.id && (
                        <Button
                            variant="contained"
                            onClick={handleStartGame}
                            sx={{ mb: 3 }}
                        >
                            Start Game
                        </Button>
                    )}

                    {game.status === 'in_progress' && 
                     game.players[game.players.length - 1]?.id === playerId &&
                     game.players[game.players.length - 1]?.completed_tasks.length > 0 &&
                     !game.players[game.players.length - 1]?.current_tasks.length &&
                     (
                        <Button
                            variant="contained"
                            color="success"
                            onClick={handleGameComplete}
                            sx={{ mb: 3 }}
                        >
                            Complete Game
                        </Button>
                    )}

                    <Grid container spacing={3}>
                        {game.players.map(renderPlayer)}
                    </Grid>
                </>
            )}
            {renderCompletionDialog()}
        </Box>
